////// Wythoff Construction

vec2 snub632() {
    // fermat point
    vec2 pf = intersection(vec2(-0.75, cos30 / 2.0), ft.zw, vec2(1, 0), ft.xy);
    // reflect over side (y-axis)
    vec2 q1 = vec2(-1.0 * pf.x, pf.y);
    // vec2 q2 = vec2(pf.x, pf.y + 2.0 * (cos30 - pf.y));
    // project to hypotenuse then double to obtain reflection
    vec2 q3 = dot(pf, ft.zw) / dot(ft.zw, ft.zw) * ft.zw;
    q3 = pf + 2.0 * (q3 - pf);
    // snub point is at half the hypotenuse of the new right triangle (Thales's theorem)
    return q1 + ((q3 - q1) / 2.0);
}

float sdSchwartz632Snub(vec3 p, float t) {
    // p the uv
    // t the thickness
    // take advantage of symmetry
    float a = atan(p.y, p.x);
    float M = floor(mod(degrees(a) - 30.0, 360.0) / 60.0) * 60.0;
    p.xy = rotmat2(radians(M)) * p.xy;
    // snub point
    vec3 g = vec3(snub632(), 0);
    // rotate
    vec3 G = vec3(rotmat2(radians(180.0)) * (g.xy - ft.xy) + ft.xy, 0);
    // distance
    float d = sdCapsule(p, g, G, t);
    // fix by https://www.shadertoy.com/user/mla
    d = opUnion(d, sdCapsule(vec3(rotmat2(radians(-60.0)) * p.xy, p.z), g, G, t));
    d = opUnion(d, sdCapsule(p, g, vec3(rotmat2(radians(-60.0)) * (G - g).xy + g.xy, 0), t));
    d = opUnion(d, sdCapsule(p, g, vec3(rotmat2(radians(+60.0)) * (G - g).xy + g.xy, 0), t));
    d = opUnion(d, sdCapsule(p, g, vec3(rotmat2(radians(+120.0)) * (G - g).xy + g.xy, 0), t));
    d = opUnion(d, sdCapsule(p, g, vec3(rotmat2(radians(+180.0)) * (G - g).xy + g.xy, 0), t));
    return d;
}

float sdSchwartz632DualSnub(vec3 p, float t) {
    // p the uv
    // t the thickness
    // snub point
    vec3 g = vec3(snub632(), 0);
    // take advantage of symmetry
    float a = atan(p.y, p.x) - (atan(g.y, g.x) - radians(60.0));
    float M = floor(mod(degrees(a), 360.0) / 60.0) * 60.0;
    p.xy = rotmat2(radians(M)) * p.xy;
    float R3 = sqrt3 / 2.0 * length(g);
    float r3 = sqrt3 / 6.0 * length(g); 
    vec2 n0 = normalize(rotmat2(radians(+30.0)) * g.xy);
    vec3 n1 = vec3((R3 + r3) * n0, 0);
    vec3 n2 = vec3(n1.xy + rotmat2(radians(-60.0)) * (2.0 * r3 * n0), 0);
    vec3 n3 = vec3(n1.xy + rotmat2(radians(+60.0)) * (2.0 * r3 * n0), 0);
    // distance
    float d = sdCapsule(p, vec3(ft.xx, 0), n1, t);
    d = opUnion(d, sdCapsule(p, n1, n2, t));
    d = opUnion(d, sdCapsule(p, n1, n3, t));
    d = opUnion(d, sdCapsule(p, n2, n1 + n2 + -n3, t));
    d = opUnion(d, sdCapsule(p, n3, n1 + n3 + -n2, t));
    return d;
}

float sdSchwartz632(vec3 p, vec2 g, float m, float w, float t, bool o) {
    // snub case
    if (m == 8.0) {
        return sdSchwartz632Snub(p, t);
    }
    // dual snub case
    else if (m == 9.0) {
        return sdSchwartz632DualSnub(p, t);
    }
    p = abs(p);
    if (p.y < 2.0 * cos30 * p.x) {
        p.xy = rotmat2(radians(-60.0)) * p.xy;
        p = abs(p);
    }
    // distance
    float d = o ? sdSphere(p, t) : MAX_DIST;
    vec3 g3 = vec3(g, 0);
    d = bool(mod(m, 2.0)) ? opUnion(d, sdCapsule(p, g3, vec3(0, g3.y, 0), t)) : d;
    vec3 v = vec3(ft.zw, 0);
    d = bool(mod(floor(m / 2.0), 2.0)) ? opUnion(d, sdCapsule(p, g3, dot(g3, v) / dot(v, v) * v, t)) : d;
    d = bool(mod(floor(m / 4.0), 2.0)) ? opUnion(d, sdCapsule(p, g3, vec3(g3.x, ft.y, 0), t)) : d;
    d = bool(mod(w, 2.0)) ? opUnion(d, sdCapsule(p, vec3(ft.xx, 0), vec3(ft.xy, 0), t)) : d;
    d = bool(mod(floor(w / 2.0), 2.0)) ? opUnion(d, sdCapsule(p, vec3(ft.xx, 0), vec3(ft.zw, 0), t)) : d;
    d = bool(mod(floor(w / 4.0), 2.0)) ? opUnion(d, sdCapsule(p, vec3(ft.xy, 0), vec3(ft.zw, 0), t)) : d;
    return d;
}

float sdSchwartz632Plane(vec3 p, vec2 g, float m, float w, float t, bool o) {
    vec3 v = vec3(3.0, 2.0 * cos30, 0.0);
    vec3 p1 = opRep(p, v);
    vec3 p2 = opRep(p + v / 2.0, v);
    return opUnion(
        sdSchwartz632(p1, g, m, w, t, o),
        sdSchwartz632(p2, g, m, w, t, o)
    );
}

//// Caspar-Klug

float sdCasparKlugFacet(vec3 p, float h, float k, vec2 g, float m, float w, float t, bool[5] x) {
    // @p vec3 the uv
    // @h float the h-parameter (steps on plane)
    // @k float the k-parameter (steps on plane after 60-degree turn)
    // @g vec2 the generator point for Wythoff construction
    // @m float the bits representing the activated mirrors for Wythoff construction
    // @w float the bits representing the fundamental triangle wall display
    // @t float the edge thickness
    // @x vec4 the config

    mat2 b = mat2(1.5, cos30, 0.0, 2.0 * cos30);           // basis
    // vec2 v1 = vec2(0);                                  // vector 1
    vec3 v2 = vec3(b * vec2(h, k), 0);                     // vector 2
    vec3 v3 = vec3(rotmat2(radians(-60.0)) * v2.xy, 0);    // vector 3
    float s = length(v2);                                  // side length
    // vec2 c = (v1 + v2 + v3) / 3.0;                      // centroid
    vec3 c = (v2 + v3) / 3.0;                              // also centroid...

    vec3 q = vec3(rotmat2(-atan(v2.y, v2.x)) * p.xy, p.z); // map p to triangle sitting on side -> ⍙
    // tile the plane
    float d = sdSchwartz632Plane(q + c, g, m, w, t, x[4]);
    // carve-out facet
    d = x[1] ? opIntersection(d, sdEquilateralTriangle(p.xy, s / (1.0 + float(x[0])))): d;

    // trace lines/paths of walk
    // TODO: simplify...
    if (x[2] || x[3]) {
        vec3 h1 = h * vec3(1.5, cos30, 0);
        mat2 h1_rot = (
            rotmat2(-atan(h1.y, h1.x)) * 
            rotmat2(-acos(dot(h1, vec3(1, 0, 0)) / (length(h1) * length(vec3(1, 0, 0)))))
        );
        for(int i = 0; i < 3; i++) {
            q.xy = rotmat2(radians(120.0)) * q.xy;
            vec3 Q = q + c - h1;
            Q.xy = h1_rot * Q.xy;
            d = x[2] ? opUnion(d, sdCapsule(q + c, vec3(0), v2, t)) : d;
            if (x[3]) {
                d = opUnion(d, sdCapsule(q + c, vec3(0), h1, t));
                d = opUnion(d, sdCapsule(q + c, h1, v2, t));
                if (h > 0.0) d = opUnion(d, sdCone(Q, vec2(0.5, 1.0), 8.0 * t));
                if (k > 0.0) d = opUnion(d, sdCone(q + c - v2, vec2(0.5, 1.0), 8.0 * t));
            }
        }
    }

    return d;
}

