void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2  uv = fragCoord;
    ivec2 iv = ivec2(uv);

    vec4 data = texelFetch(iChannel1, iv, 0);

    bool is_shift = keyDown(16/* shift */);
    bool is_ctrl  = keyDown(17/* ctrl  */);

    if (iFrame == 0) {
        /**/ if (iv == IDX_PARA_1) data = vec4( 2,  1, 0, 8); // h, k, wythoff, thickness
        else if (iv == IDX_PARA_2) data = vec4(5, 12, 0, 0); // fiber, zoom, _, _
        else if (iv == IDX_MODE_1) data = vec4(MODE_ICOSA, -MODE_LINES, -MODE_PATHS, -MODE_RANDO);
        else if (iv == IDX_MODE_2) data = vec4(MODE_POINT, -MODE_RSPIN, 0, 0);
    } else {
        /**/ if (iv == IDX_PARA_1 && !(is_ctrl)) {
            // Caspar-Klug
            /**/ if (keyClick(72/* h */)) data.r += 1.0 * (is_shift ? -1.0 : 1.0);
            else if (keyClick(75/* k */)) data.g += 1.0 * (is_shift ? -1.0 : 1.0);
            data.r = data.r < 0.0 ? 0.0 : data.r;
            data.g = data.g < 0.0 ? 0.0 : data.g;
            // Wythoff Construction
            float offset = is_shift ? float(TILINGS.length() / 2) : 0.0;
            for (int i = 0; i < TILINGS.length() / 2; i++) 
                if (keyClick(i + 49 /* 1 */))
                    data.b = float(i) + offset;
            // thickness
            if (keyClick(84/* t */)) data.a += 1.0 * (is_shift ? -1.0 : 1.0);
            data.a = data.a < 1.0 ? 1.0 : data.a;
        }
        else if (iv == IDX_PARA_2 && !(is_ctrl)) {
            /**/ if (keyClick(70/* f */)) data.r += 1.0 * (is_shift ? -1.0 : 1.0); // fiber
            else if (keyClick(90/* z */)) data.g += 1.0 * (is_shift ? -1.0 : 1.0); // zoom
            data.r = data.r < 0.0 ? 0.0 : data.r;
        }
        else if (iv == IDX_MODE_1) {
            if (is_ctrl) {
                /**/ if (keyClick(49/* 1 */)) data.r = MODE_ICOSA;
                else if (keyClick(50/* 2 */)) data.r = MODE_FACET;
                else if (keyClick(51/* 3 */)) data.r = MODE_PLANE;
            } else {
                /**/ if (keyClick(76/* l */)) data.g *= -1.0; // lines
                else if (keyClick(80/* p */)) data.b *= -1.0; // paths
                else if (keyClick(82/* r */)) data.a *= -1.0; // rando
            }
        }
        else if (iv == IDX_MODE_2) {
            /**/ if (keyClick(67/* c */)) data.r *= -1.0; // point
            else if (keyClick(83/* s */)) data.g *= -1.0; // rspin
        }
    }

    fragColor = data;
}
