#define t iTime*4.
#define r iResolution
#define iterations 250 //number of raymarching steps
#define EPSILON .001
#define MAX_DIST 40.0
#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))


//Based on https://www.shadertoy.com/view/3tXyRN

vec3 spaghetti(vec3 uv)
{
    vec3 col = vec3(0.);
    for(int k = 0; k < 6; k++){ 
        vec3 offset =
            //fract(vec3(uv.x-.45,uv.y+.45,uv.z-uv.x))
            vec3(uv.x+.35,uv.y+.35,uv.z-uv.x)
        ;
        uv =
            abs(fract(uv.yzx+offset.zxy)-.5)
            //min(offset,abs(fract(uv.yzx+offset.zxy)-.5))
        ;
        //uv.yx *= rot(uv.y/8.);
        col = vec3(col.yz,uv.x-uv.y);
    }

    return
        col
        //col - uv;
    ;
}

void mainImage( out vec4 o, in vec2 U )
{
    o = vec4(0.0);
    vec3 d=vec3(U/r.xy-.5,.5),p0=vec3(0,6,t),p=p0;
    if (length(iMouse.xy) > 40.0) {
        d.yz *= rot(-3.14*0.5+iMouse.y/iResolution.y*3.14);
        d.xz *= rot(-3.14+iMouse.x/iResolution.x*3.14*2.0);
    }
    float l1;
    vec3 col = vec3(0.);
    for(int i=0;i<iterations;i++)
    {
        col = spaghetti(p/16.);
        float sd1 = col.z-.14;
        if(-sd1 < EPSILON*(length(l1)+1.0) || l1 > MAX_DIST){ break; }
        p+=d*(-sd1);
        l1 = distance(p,p0);
    }
    o+=(vec4(col/2.,0)+l1/100.0)*3.0;
}

