#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(float i = 0.; i < 3.; i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5021333;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*((sin(z)+1.)*abs(sin(l*9.-z-z)) + tan(exp(1.61803398875 * 3.141592653589)) + cos(exp(6.62607015)));
        uv += i + t * 0.5;
        int j = int(i);
		c[j]=.01/length(mod(uv,1.)-.5) + (sin(exp(i + t)) + 1.) * 0.01;
	}
	fragColor=vec4((c/l),t);
}
