#define PI 3.14159265359
#define TWO_PI 6.28318530718

//uniform vec2 u_resolution;
//uniform float u_time;

float circle(in vec2 _st, in float _radius){
    vec2 l = _st-vec2(0.5);
    return (1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(l,l)*4.0));
}

float triangle(in vec2 st){

  // Remap the space to -1. to 1.
  st = st *2.-1.;

  // Number of sides of your shape
  int N = 3;

  // Angle and radius from the current pixel
  float a = atan(st.x,st.y)+PI;
  float r = TWO_PI/float(N);

  // Shaping function that modulate the distance
  float d = cos(floor(.5+a/r)*r-a)*length(st);

  return (1.0-smoothstep(.4,.41,d));

}

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = vec2(0.5)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
    return uv.x*uv.y;
}


vec2 tile(in vec2 st, float space){
    return vec2(fract(st.x*space), fract(st.y*space));
    // return vec2(fract(st.x*space), st.y);
    // return vec2(st.x, fract(st.y*space));
    // return vec2(st.x,st.y);
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 st = fragCoord.xy / iResolution.xy;
    st.x *= iResolution.x / iResolution.y;
    vec3 color = vec3(0.0);

    st = tile(st, 4.0);
    st = rotate2D(st, PI*.25*iTime);


    // color = vec3(st,0.0);
    // color = vec3(circle(st,0.5));
    // color = vec3(circle(tile(st, 9.0),0.5));
    // color = vec3(triangle(st));
    if(mod(st.x, 2.0) == 1.0) {
        color = vec3(box(st, vec2(.7), .1), mod(fract(iTime)*.5, 2.), sin(iTime));
    }else if(st.y >= .2) {
        color = vec3(triangle(st), mod(fract(iTime)*.5, 2.), sin(iTime));
    }

	fragColor = vec4(color.x, color.y, abs(sin(iTime)), 1.0);
}
