#define Bloom

//I used the Bing AI to improve this code.
vec3 blur(sampler2D s, vec2 p, int w) {
  vec2 invRes = 1. / vec2(textureSize(s, 0));
  vec3 color = vec3(0.);
  float n = 1./float(2*w*w);
  const float invpi = 1./PI;
  for (int x = -w; x <= w; x++) {
    for (int y = -w; y <= w; y++) {
      vec2 uv = p + vec2(x, y) * invRes;
      float weight = n*invpi*exp(-float(x*x+y*y)*n);
      //float weight = 1.;
      color += texture(s, uv).xyz*weight;
    }
  }
  return color;
}

#define size 2.75*((R.x+R.y)*.00119048)
void mainImage( out vec4 o,  vec2 i )
{
    vec2 p = R-10.;
    if(i.x>=p.x&&i.y>=p.y&&iFrame>maxFrame){o=vec4(1.);return;}
    o = texelFetch(iChannel0,ivec2(i),0);
    o /= o.w;

    #ifdef Bloom
    #ifdef maxFrame
    if(iFrame>=maxFrame)
    #endif
    o.xyz += blur(iChannel1,i/R,int(size));
    #endif

    //Comment out the above and uncomment the below to see what will be bloomed.
    //o.xyz = texelFetch(iChannel1,ivec2(i),0).xyz;

    o /= o+1.;
    o = pow(o,vec4(1./2.2));
}

#define R iResolution.xy
#define PI 3.1415926523
#define d2r(a) a*(PI/180.)

#define maxFrame 1000

float _seed;
float rand(void) {
    _seed += 1.;
    return fract(81.29*sin(38.23*+_seed-12.13)-12.21);
}
vec2 rand2(void) {return vec2(rand(),rand());}
float srand(float s) {
    return fract(37.12*sin(91.04*s-76.82)-19.35);
}
void irand(vec2 c,float f){
    _seed = srand(f)*f+srand(c.x)*c.y+srand(c.y)*c.x;
}

mat3 rot3(vec3 a){
    vec3 c = cos(a);
    vec3 s = sin(a);

    mat3 xm = mat3(1., 0., 0.,
                   0., c.x, -s.x,
                   0., s.x, c.x );

    mat3 ym = mat3(c.y, 0.,-s.y,
                   0.,1.,0.,
                   s.y,0.,c.y);

    mat3 zm = mat3(c.z,s.z,0.,
                   -s.z,c.z,0.,
                   0.,0.,1.);


    return xm*ym*zm;
}


struct Mat {
    float r;
    vec3 col;
    vec3 ems;
    float spch;
    vec3 spcl;
    float refidx;
    bool refq;
};


struct AABB {
    vec3 minv,maxv;
};

struct SphBody {
    vec3 c;
    float rad;
    bool isActive;
};

struct BoxBody {
    vec3 pos,size,rot;
    bool isActive;
};

struct QuadBody {
    vec3 v0,v1,v2,v3;
    bool isActive;
};

struct Obj {
    SphBody sph;
    BoxBody box;
    QuadBody quad;
    Mat mat;
    AABB bound;
};

struct BVHNode {
    AABB bound;
    uint left,right;
};

struct Viewer {
    vec3 uu;
    vec3 vv;
    vec3 ww;
    vec3 ro;
};

struct NewHit {
    float len;
    vec3 nor;
    Obj o;
    bool ff;
};

struct IntRes {
    float len;
    bool ff;
};

#define DefaultSph SphBody(vec3(0.),0.,false)
#define DefaultBox BoxBody(vec3(0.),vec3(0.),vec3(0.),false)
#define DefaultQuad QuadBody(vec3(0.),vec3(0.),vec3(0.),vec3(0.),false)

#define objC 9
struct NewWorld {
    Obj objs[objC];
    Viewer camera;
};

Obj Obj_Sphere(vec3 pos,float rad,Mat m){
    AABB bound = AABB(pos-rad-.0001,pos+rad+.0001);
    return Obj(SphBody(pos,rad,true),DefaultBox,DefaultQuad,m,bound);
}

void createObj_HollowSphere(vec3 pos,float rad,float thickness,int idx0,int idx1,Mat m,inout Obj objlst[objC]){
    objlst[idx0] = Obj_Sphere(pos,rad,m);
    objlst[idx1] = Obj_Sphere(pos,thickness-rad,m);
}

Obj Obj_Box(vec3 pos,vec3 size,vec3 rot,Mat m){
    AABB bound = AABB(pos-2.*size-.001,pos+2.*size+.0001);
    return Obj(DefaultSph,BoxBody(pos,size,rot,true),DefaultQuad,m,bound);
}

Obj Obj_Quad(vec3 v0,vec3 v1,vec3 v2,vec3 v3,Mat m){
    vec3 minv = min(min(v0,v1),min(v2,v3));
    vec3 maxv = max(max(v0,v1),max(v2,v3));
    AABB bound = AABB(minv,maxv);
    return Obj(DefaultSph,DefaultBox,QuadBody(v0,v1,v2,v3,true),m,bound);
}

IntRes newSphereInt( in vec3 ro, vec3 rd, SphBody sph) {
    ro -= sph.c;
    float b = dot( ro, rd );
    float c = dot( ro, ro ) - sph.rad*sph.rad;
    float h = b*b - c;
    if( h<0. ) return IntRes(-1.,false);
    h = sqrt( h );
    if(length(ro)<=sph.rad){
        return IntRes(-b+h,false);
    }else{
        return IntRes(-b-h,true);
    }
}

bool insideAabb(vec3 p,vec3 s){
    return (p.x<=s.x&&p.x>=-s.x)&&(p.y<=s.y&&p.y>=-s.y)&&(p.z<=s.z&&p.z>=-s.z);
}

IntRes newBoxInt( in vec3 ro, in vec3 rd, BoxBody box) {
    mat3 r = rot3(-box.rot);
    ro -= box.pos;
    ro, rd *= r;
    vec3 m = 1./rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*box.size;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.) return IntRes(-1.,false);
    if(insideAabb(ro,box.size)){
        return IntRes(tF,false);
    }else{
        return IntRes(tN,true);
    }
}

float newQuadInt(vec3 ro,vec3 rd,QuadBody q){
    vec3 n = cross(q.v1-q.v0,q.v2-q.v0);
    float d = dot(n, q.v0);

    float nd = dot(n, rd);
    if (abs(nd) < 1e-6) return -1.;

    float t = (d - dot(n, ro)) / nd;
    if (t < 0.) return -1.;
    vec3 p = ro + t * rd;

    vec3 e0 = q.v1 - q.v0;
    vec3 e1 = q.v2 - q.v1;
    vec3 e2 = q.v3 - q.v2;
    vec3 e3 = q.v0 - q.v3;
    vec3 c0 = p - q.v0;
    vec3 c1 = p - q.v1;
    vec3 c2 = p - q.v2;
    vec3 c3 = p - q.v3;
    if (dot(n, cross(e0, c0)) > 0. &&
        dot(n, cross(e1, c1)) > 0. &&
        dot(n, cross(e2, c2)) > 0. &&
        dot(n, cross(e3, c3)) > 0.) {
        return t;
    }

    return -1.;
}

bool aabbInt(in vec3 ro,vec3 rd,AABB box){
    vec3 a = .5*(box.maxv+box.minv);
    vec3 d = .5*(box.maxv-box.minv);
    vec3 m = 1./rd;
    vec3 n = m*(ro-a); 
    vec3 k = abs(m)*d;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.) return false;
    return true;
}



const float bias = 1.0001;
vec3 newBoxNor(vec3 p,BoxBody b){
    vec3 q = p-b.pos;
    vec3 d = b.size;
    return normalize(vec3(ivec3(q/d*bias)));
}

vec3 cosDir(vec3 nor)
{
    float u = rand();
    float v = rand();
    float a = 2.*PI*v; float b = 2.*u-1.;
    vec3 dir = vec3(sqrt(1.-b*b)*vec2(cos(a),sin(a)),b);
    return normalize(nor+dir);
}
vec3 uniformVec(void)
{
    float phi = rand()*2.*PI;
    float x = rand()*2.-1.;
    float z = rand();
	return pow(z,1./3.)*vec3(sqrt(1.-x*x)*vec2(sin(phi),cos(phi)),x);
}
mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2( c,-s,
                 s, c);
}
float reflectance(float cosv,float refidx){
    float r0 = (1.-refidx)/(1.+refidx);
    r0 *= r0;
    return r0+(1.-r0)*pow(1.-cosv,5.);
}

//NOTE: When making any changes in the code in this tab, and the mode is zero,
//you must reset the time for the changes to take effect.
#define shutAp .6
#define fcsDist 4.5
#define blurAmt .048
#define numLvls 4
#define spp 1.
#define FOV 75.
#define fovInDeg 1
#define BeautifulSky
const float mode = 0.;
//mode=0.: Develop; the image develops over multiple frames.
//mode=1.; Realtime; the image is signifigantly more noisy, but changes take effect
//immediatly.
//Any other value of mode: FPS; same as Develop, but resets the image every
//->mode<- frames. FPS MODE NOT RECCOMENDED. Develop mode reccomended.

//To add objects, look in the 'Common' tab.


void sort(inout AABB boxes[2*objC-1],int axis){
    float vs[boxes.length()];
    if(axis==0) for(int i = 0; i<boxes.length();i++){
        vs[i] = boxes[i].minv.x;
    }
    else if(axis==1) for(int i = 0; i<boxes.length();i++){
        vs[i] = boxes[i].minv.y;
    }
    else for(int i = 0; i<boxes.length();i++){
        vs[i] = boxes[i].minv.z;
    }

    float avrg = 0.;
    for(int i = 0; i < boxes.length(); i++) {
        avrg += vs[i];
    }
    avrg /= float(boxes.length());

    int highid = 0;
    int lowid = 0;
    AABB high[boxes.length()/2];
    AABB low[boxes.length()-high.length()];
    for(int i = 0; i < boxes.length(); i++) {
        if(vs[i]<=avrg) {low[lowid] = boxes[i];lowid++;}
        else {high[highid] = boxes[i];highid++;}
    }
    int boxid = 0;
    for(int i = 0; i < low.length(); i++) {
        boxes[boxid] = low[i];
        boxid ++;
    }
    for(int i = 0; i < high.length(); i++) {
        boxes[boxid] = high[i];
        boxid ++;
    }
}

NewWorld getNewWorld(float t){
    NewWorld world;
    Viewer cam = Viewer(vec3(1.,0.,0.),vec3(0.,1.,0.),vec3(0.,0.,1.),vec3(1.5,2.,-2.));

    mat2 rx = rot(d2r(0.));
    cam.uu.zy *= rx;
    cam.vv.zy *= rx;
    cam.ww.zy *= rx;

    mat2 ry = rot(-d2r(0.));
    cam.uu.xz *= ry;
    cam.vv.xz *= ry;
    cam.ww.xz *= ry;

    mat2 rz = rot(-d2r(0.));
    cam.uu.yx *= rz;
    cam.vv.yx *= rz;
    cam.ww.yx *= rz;

    world.objs[0] = Obj_Sphere(vec3(0.,-1002.001,0.),1000.,Mat(.75,vec3(.3,.4,1.),vec3(0.),.5,vec3(.2,.5,.15),1.52,false));
    createObj_HollowSphere(vec3(-1.5,-.5,3.),1.5,.4,1,8,Mat(.2,vec3(1.),vec3(0.),0.,vec3(1.),1.52,true),world.objs);
    world.objs[2] = Obj_Sphere(vec3(1.5,-1.,2.),1.,Mat(.8,vec3(1.),vec3(0.),0.,vec3(1.),1.52,true));
    world.objs[3] = Obj_Quad(vec3(-3.5,-2.,-2.),vec3(3.5,-2.,-2.),vec3(3.5,-2.,3.5),vec3(-3.5,-2.,3.5),Mat(.7,vec3(1.),vec3(0.),0.,vec3(1.),1.52,false));
    world.objs[4] = Obj_Quad(vec3(-3.5,-2.,-2.),vec3(-3.5,5.,-2.),vec3(-3.5,5.,3.5),vec3(-3.5,-2.,3.5),Mat(.5,vec3(1.,0.,0.),vec3(0.),0.,vec3(1.,0.,0.),1.52,false));
    world.objs[5] = Obj_Quad(vec3(3.5,-2.,-2.),vec3(3.5,5.,-2.),vec3(3.5,5.,3.5),vec3(3.5,-2.,3.5),Mat(.5,vec3(0.,1.,0.),vec3(0.),0.,vec3(0.,1.,0.),1.52,false));
    world.objs[6] = Obj_Quad(vec3(-3.5,5.,-2.),vec3(3.5,5.,-2.),vec3(3.5,5.,3.5),vec3(-3.5,5.,3.5),Mat(0.,vec3(1.),vec3(0.),0.,vec3(1.),1.52,false));
    world.objs[7] = Obj_Box(vec3(0.,4.,.5),vec3(.75,.25,.75),d2r(vec3(0.)),Mat(.5,vec3(.75,.775,.9),vec3(5.),0.,vec3(0.),1.52,false));

    //TODO: Generate bounding boxes


    world.camera = cam;
    return world;
}

NewHit newInt(vec3 ro,vec3 rd,Obj objs[objC]){
    NewHit h;
    h.len = -1.;

    for(int i = 0; i < objC; i++) {
        Obj o = objs[i];
        if(!aabbInt(ro,rd,o.bound)) continue;
        if(o.sph.isActive) {IntRes res = newSphereInt(ro,rd,o.sph);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.box.isActive) {IntRes res = newBoxInt(ro,rd,o.box);if(res.len>=.0001&&(res.len<h.len||h.len<0.)){h.o=o;h.ff=res.ff;h.len=res.len;}}
        else if(o.quad.isActive) {float res = newQuadInt(ro,rd,o.quad);if(res>=.0001&&(res<h.len||h.len<0.)){h.o=o;h.ff=true;h.len=res;}}
    }

    Obj o = h.o;
    vec3 p = ro+rd*h.len;

    if(o.sph.isActive) h.nor = normalize(p-o.sph.c);
    else if(o.box.isActive) {BoxBody b = o.box;mat3 r = rot3(b.rot);mat3 rn = rot3(-b.rot);h.nor = newBoxNor(b.pos+(p-b.pos)*rn,b)*r;}
    else if(o.quad.isActive) {QuadBody q = o.quad;h.nor = normalize(cross(q.v1-q.v0,q.v2-q.v0));}

    h.nor *= h.ff?1.:-1.;

    return h;
}

const vec3 sunDir = normalize(vec3(.5,1.,.75));
//Sebastian Lagues's sky function
vec3 getSky(float t,vec3 rd) {
    #ifdef BeautifulSky
    float skyGradT = pow(smoothstep(0.,.4,rd.y),.35);
    vec3 skyGrad = mix(vec3(.05),vec3(.3,.6,.9),skyGradT);

    vec3 sun = pow(max(0.,dot(rd,sunDir)),40.)*10.*vec3(1.,.85,.7);

    float groundT = smoothstep(-.01,0.,rd.y);

    return mix(vec3(.25),skyGrad,groundT)+sun;
    #else
    return vec3(0.);
    #endif
}

vec3 newRay(inout vec3 ro,inout vec3 rd,vec3 p,vec3 n,Mat m,float isSpec,bool ff) {
    vec3 oro = ro; vec3 ord = rd;
    ro = p;
    if(m.refq){
        float refrat = ff?1./m.refidx:m.refidx;
        float cosT = max(dot(-rd,n),0.);
        float sinT = sqrt(1.-cosT*cosT);
        if(refrat*sinT<=1.&&reflectance(cosT,refrat)<=rand()) rd = refract(rd,n,refrat);
        else rd = reflect(rd,n);
    }else{
        vec3 reflected = reflect(rd,n);
        rd = normalize(mix(reflected,cosDir(reflected),m.r*(1.-isSpec)));
    }
    return mix(m.col,m.spcl,isSpec);
}

void mainImage( out vec4 o, in vec2 i ){
    #ifdef maxFrame
    if(iFrame>=maxFrame) {o=texelFetch(iChannel0,ivec2(i),0);return;}
    #endif
    irand(i,float(iFrame));

    float ftime;
    if(mode==0.){
        o = texelFetch(iChannel0,ivec2(i),0);
        ftime = 0.;
    } else if(mode==1.) {
        o = vec4(0.);
        ftime = iTime;
    }else{
        if(iTimeDelta*mode>1.) {
        o = vec4(0.);
        }else{
        o = texelFetch(iChannel0,ivec2(i),0);
        }
        ftime = floor(iTime*mode)/mode;
    }
    if(texture(iChannel3, vec2(32.5/256.0,0.25)).x > 0.1) o = vec4(0.);

    #if fovInDeg==0
    float dv = 1./tan(FOV/2.);
    #else
    float dv = 1./tan(PI*FOV/360.);
    #endif

    vec3 ttcol = vec3(0.);
    for(float _i = 0.; _i<spp;_i++) {
        vec2 p1 = (-R+ 2.*(i + rand2())) / R.y;

        vec3 er = normalize(vec3(p1,dv));
        float ctime = ftime - rand()*shutAp/24.;

        NewWorld world = getNewWorld(ctime);
        Viewer cam = world.camera;

        vec3 rd = er.x*cam.uu + er.y*cam.vv+er.z*cam.ww;

        vec3 go = blurAmt*vec3(-1.+2.*rand2(),0.);
        vec3 gd = normalize( er*fcsDist-go );
        cam.ro += go.x*cam.uu + go.y*cam.vv;
        rd += gd.x*cam.uu + gd.y*cam.vv;
        rd = normalize(rd);

        vec3 tcol = vec3(0.);
        vec3 fcol = vec3(1.);
        for(int _j = 0; _j<numLvls;_j++) {
            NewHit hit = newInt(cam.ro,rd,world.objs); 
            if(hit.len==-1.) {tcol+=getSky(ctime,rd)*fcol;break;}                             

            vec3 p = hit.len*rd+cam.ro;

            Mat mat = hit.o.mat;

            float isSpec = mat.spch>rand()?1.:0.;

            tcol += mat.ems*fcol;
            fcol *= newRay(cam.ro,rd,p,hit.nor,mat,isSpec,hit.ff);

            #if 1
            {
                float r_p = max(fcol.x,max(fcol.y,fcol.z));
                if(rand()>r_p) break;
                fcol /= r_p;
            }
            #endif
        }

        ttcol += tcol;
    }
    if(ttcol.x!=ttcol.x) ttcol.x = 0.;
    if(ttcol.y!=ttcol.y) ttcol.y = 0.;
    if(ttcol.z!=ttcol.z) ttcol.z = 0.;
    o += vec4(ttcol / spp,1.);
}

void mainImage( out vec4 o, vec2 i )
{
    o = texture(iChannel0,i/R);
    o /= o.w;
    float d = dot(o.xyz,vec3(1.));
    o.xyz = d>4.?o.xyz:vec3(0.);
}
