#define DEPTH 5
#define OBJECT_COUNT 5

const vec3 up = vec3(0, 1, 0);

struct material
{
    float metallic;
    float roughness;
    float specular;
    vec3 col;

};

struct sphere
{
    vec3 pos;
    float radius;
    material mat;
};

struct box
{
    vec3 pos;
    vec3 up; // Length of up is scale
    material mat;
};

struct plane
{
    vec3 pos;
    vec3 normal;
    material mat;
};

struct rayHit
{
    float t1;
    float t2;
    vec3 pos;
    vec3 normal;
    vec3 incoming;
    vec3 reflection;
    bool hit;
    vec3 origin;
    material mat;
};

const float pi = 3.14159;
float cameraFarPlane = 1e8;
float fieldOfView = 2.;
float mouseSensitivity = 2.;




// Materials //

    const material testBlue = material(
        0.0,                // Metallic 
        0.0,               // Roughness
        0.5,              // Specular
        vec3(.1, .3, .8) // Colour
    );


    const material testRed = material(
        0.0,                // Metallic 
        0.5,               // Roughness
        0.1,              // Specular
        vec3(.8, .2, .2) // Colour
    );

    const material testGreen = material(
        1.,                 // Metallic 
        0.,                // Roughness
