// General functions // 

//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


vec2 circleSample(uvec3 x){
    vec2 v,z;
    for(int i = 0;v=hash(x+uint(v*1e9)).xy, z=v*2.-1., i++<9 && length(z) > 1.;);
    return z;
}

mat3 matTowards( vec3 n ){

    vec3 r = normalize(abs(dot(n, up)) < 1e-2?cross(n, up):cross(n, normalize(vec3(1))));
    vec3 f = cross(r, n);

    return mat3(r, f, n); 
}

vec3 cosDist( uvec3 x, vec3 norm ){ // Cosine weighted distribution
    vec2 u = circleSample(x);
    return vec3(u, sqrt(1.-u.x*u.x-u.y*u.y)) * matTowards(norm);
}

vec3 background(float t, vec3 rd)
{
	vec3 light = normalize(vec3(sin(t), 0.6, cos(t)));
	float sun = max(0.0, dot(rd, light));
	float sky = max(0.0, dot(rd, vec3(0.0, 1.0, 0.0)));
	float ground = max(0.0, -dot(rd, vec3(0.0, 1.0, 0.0)));
	return 5.*(
		(pow(sun, 256.0)+0.2*pow(sun, 2.0))*vec3(2.0, 1.6, 1.0) +
		pow(ground, 0.5)*vec3(0.4, 0.3, 0.2) +
		pow(sky, 1.0)*vec3(0.5, 0.6, 0.7));
}








// Intersection functions

rayHit sphereRay(vec3 o, vec3 dirV, sphere sp)
{    
    rayHit hit;
    hit.incoming = dirV;

    float t = dot(sp.pos - o, dirV);
    vec3 p = o + dirV*t;

    float y = length(sp.pos - p);

    hit.t1 = cameraFarPlane;
    hit.t2 = cameraFarPlane;

    if (y < sp.radius && t > 0.) //hit && in front of camera
    {
        float x = sqrt(sp.radius*sp.radius - y*y);
        hit.t1 = t-x;
        hit.t2 = t+x;

        vec3 p1 = o + dirV*hit.t1;
        vec3 n = normalize(p1 - sp.pos);
        hit.normal = n;
        hit.hit = true;
        hit.pos = p1;
        hit.mat = sp.mat;
        hit.origin = sp.pos;
    }
    else if (y < sp.radius)
    {
        hit.hit = false;
    }
    return hit;

}

rayHit planeRay(vec3 o, vec3 dirV, plane p)
{
    rayHit hit;
    hit.incoming = dirV;
    hit.hit = false;

    float dist = -(dot(p.normal, p.pos-o));

    if (dist > 0.0)
    {
        dist /= dot(dirV, -p.normal);


        hit.t1 = cameraFarPlane;
        hit.t2 = cameraFarPlane;

        if (dist > 0.0)
        {
            hit.t1 = dist;
            hit.hit = true;
            hit.pos = o + hit.t1 * dirV;
            hit.normal = p.normal;
            hit.origin = p.pos;
            hit.mat = p.mat;
        }
    }

    return hit;

}

rayHit boxRay(vec3 o, vec3 dirV, box b)
{

    rayHit hit;
    hit.incoming = dirV;
    hit.t1 = cameraFarPlane;
    hit.t2 = cameraFarPlane;
    hit.hit = false;

    rayHit tempHits[6];
    rayHit minHit;

    float minDist = cameraFarPlane;


    vec3 dirs[6];
    dirs[0] = normalize(b.up);
    dirs[1] = b.up==vec3(0,0,1)?vec3(1, 0, 0):normalize(cross(b.up, vec3(0, 0, 1)));
    dirs[2] = normalize(cross(b.up, dirs[1]));
    dirs[3] = -dirs[0];
    dirs[4] = -dirs[1];
    dirs[5] = -dirs[2];
    float scale = length(b.up);

    for (int i = 0; i<6; i++)
    {
        dirs[i] = dirs[i]*(scale-0.001);
        plane p;
        p.pos = dirs[i]; p.normal = dirs[i];
        tempHits[i] = planeRay(o-b.pos, dirV, p);

    }

    for (int i = 0; i<6; i++)
    {
        rayHit hi = tempHits[i];
        if (hi.hit 
        &&  max(abs(dot(hi.pos, dirs[0])), 
            max(abs(dot(hi.pos, dirs[1])), 
                abs(dot(hi.pos, dirs[2])))) < (scale*scale))
        {
            hit = hi;
            hit.normal = normalize(hit.normal);
            hit.pos += b.pos;
            hit.origin = b.pos;
        }

    }

    hit.mat = b.mat;

    return hit;

}

rayHit pew(vec3 o, vec3 dirV, plane p){
    return planeRay(o, dirV, p);

}

rayHit pew(vec3 o, vec3 dirV, box b){
    return boxRay(o, dirV, b);
}

rayHit pew(vec3 o, vec3 dirV, sphere s){
    return sphereRay(o, dirV, s);
}


rayHit ray(vec3 o, vec3 dirV, float t){


    // Animation //

    sp1.pos.xy = vec2(cos(t + vec2(0, 11))) * vec2(1, .6) - vec2(1,0);
    sp1.pos.z = -.5;

    bo1.up = normalize(vec3(cos(t * vec3(1,3,2)*.4)))*.5;
    bo1.pos.x += sin(t)*.5;

    ///////////////




    rayHit hit;
    hit.incoming = dirV;
    hit.t1 = cameraFarPlane;
    hit.t2 = cameraFarPlane;
    hit.hit = false;

    rayHit hits[OBJECT_COUNT];


    // Rendering //

    hits[0] = pew(o, dirV, sp1);
    hits[1] = pew(o, dirV, pl1);
    hits[2] = pew(o, dirV, bo1);
    hits[3] = pew(o, dirV, bo2);
    hits[4] = pew(o, dirV, sp2);


    ///////////////


    // Pick the closest hit
    for (int i = 0; i < OBJECT_COUNT; i++){        
        if (hits[i].hit && hits[i].t1 < hit.t1) hit = hits[i];
    } 
    hit.reflection = reflect(hit.incoming, hit.normal);
    return hit;
}

#define time iTime
//47.9
#define bgTime iTime

void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = iMouse.xy==vec2(0)?vec2(0):(2.*iMouse.xy-r)/r.y;


    vec3 o = vec3(0, 0, -2.5);

    vec3 camDir = vec3(sin(muv.x)*cos(muv.y), sin(muv.y), cos(muv.x)*cos(muv.y));

    vec3 camRight = normalize(cross(up, camDir));
    vec3 camUp = cross(camDir, camRight);

    vec3 dirV = normalize(camDir + camRight * cuv.x + camUp * cuv.y);


    rayHit hit;
    rayHit hits[DEPTH];

    vec3 col = vec3(1);
    vec3 light = vec3(0);

    for (int i = DEPTH; i-->0;){


        hit = ray(o, dirV, time);
        hits[i] = hit;

        if (hit.hit){
            vec3 h = hash(uvec3(U.y, iFrame, U.x));
            if (h.x < hit.mat.specular*(1.-pow(dot(hit.normal, -hit.incoming), 5.))){
                //Specular
            } else {
                if (h.y < hit.mat.metallic){
                    //Metallic
                    col *= hit.mat.col;
                } else {
                    // Dielectric
                    col *= hit.mat.col;
                    hit.mat.roughness = 1.;
                }
            }

            if (i == 0) col *= 0.;
        } else {
            light = background(bgTime, dirV);
            break;
        }

        o = hit.pos;
        dirV = mix(hit.reflection, cosDist( uvec3(iFrame, U.y, U.x), hit.normal), hit.mat.roughness);
    }

    col *= light;
    O.xyz = col;




    hit = hits[DEPTH-1];
    // Temporal Accumulation

    if (hit.hit && iFrame > 10){
        O.w = hash(uvec3((abs(hit.pos + hit.normal + hit.incoming)) * 1e7)).z;
    } else {
        O.w = mod(iTime, 1.);

        O.xyz = background(bgTime, dirV);
    }

    vec4 last = texture(iChannel0, uv);
    float change = float(bool(abs(O.w - last.w)));


    O.xyz = mix(last.xyz, O.xyz, min(1., mix(change, 1., 0.01)));

    //O.xyz = vec3(float(bool(last.w - O.w)));
}
