    float h = hash(vec2(r * vec2( hash(id))) + vec2( hash(id) ));
    if (h < 0.5)
        time = r * PI * 3.;//r;// - .2;
    return min(max(sin(time  ), -0.2 ), 0.2 ) * 5.;
 }



