float e(in vec2 uv )
{
    uv *= 1.5;
    vec2 id = floor(uv);

    float r = rand_trigger(uv.xy, id, 1.0);
    uv = fract(uv) - vec2(.5);
      r = min(1.0, r);
    r = max(-0.0, r);
    float angle = 3.14 * r / 2.;
    uv *= rotate2d(angle);
    int si = hashi(id) % 2 == 0 ? -1 : 1;

    float s = truchet(uv + vec2(.5), float(si));
    vec3 col = vec3( s );
    return col.x;
}

