float scene(vec3 p) {
    // dynamic scene parameters
    //// Caspar-Klug
    float h = mod(round((iTime + 3.0 / 2.0) / 2.0), 3.0) + 1.0;
    float k = mod(round((iTime + 6.0 + 3.0 / 2.0) / 3.0), 3.0);
    //// Wythoff
    ////// pick point within fundamental triangle
    ////// https://mathworld.wolfram.com/TrianglePointPicking.html
    ////// http://extremelearning.com.au/evenly-distributing-points-in-a-triangle/
    float a1 = 0.5 + sin(0.25 * iTime / 1.5) / 2.0;
    float a2 = 0.5 + cos(2.00 * iTime / 1.5) / 2.0;
    ////// generator point
    vec2 g = (((a1 + a2) < 1.0) ? (a1 * v3 + a2 * v2) : ((1.0 - a1) * v3 + (1.0 - a2) * v2));
    ////// mirrors
    float m = mod(round((iTime - 2.0 / 1.5) / 2.0), 10.0);
    ////// walls
    float w = 0.0;

    float t = 0.0025;
    float s = distance(TET_V[0], TET_V[1]) / length(mat2(1.5, cos30, 0.0, 2.0 * cos30) * vec2(h, k));    

    // distance
    vec3 c1 = (TET_V[0].xyz + TET_V[1].xyz + TET_V[2].xyz) / 3.0;
    vec3 c2 = (TET_V[1].xyz + TET_V[4].xyz + TET_V[2].xyz) / 3.0;
    vec3 c3 = (TET_V[0].xyz + TET_V[2].xyz + TET_V[4].xyz) / 3.0;
    vec3 c4 = (TET_V[0].xyz + TET_V[4].xyz + TET_V[1].xyz) / 3.0;
    p = rotmat3(vec3(iTime * 0.25)) * p;
    vec4 q = M1 * vec4(p - c1, 1);
    float d = opExtrusion(q.z, sdCasparKlugFacet(q.xy / s, h, k, g, m, w, t) * s, t);
    q = M2 * vec4(p - c2, 1);
    d = min(
        d,
        opExtrusion(q.z, sdCasparKlugFacet(q.xy / s, h, k, g, m, w, t) * s, t)
    );
    q = M3 * vec4(p - c3, 1);
    d = min(
        d,
        opExtrusion(q.z, sdCasparKlugFacet(q.xy / s, h, k, g, m, w, t) * s, t)
    );
    q = M4 * vec4(p - c4, 1);
    d = min(
        d,
        opExtrusion(q.z, sdCasparKlugFacet(q.xy / s, h, k, g, m, w, t) * s, t)
    );

    return d;
}

float march(vec3 ro, vec3 rd) {
  float depth = MIN_DIST;
  float d;

  for (int i = 0; i < MAX_ITER; i++) {
    vec3 p = ro + depth * rd;
    d = scene(p);
    depth += d;
    if (d < PRECISION || depth > MAX_DIST) break;
  }

  d = depth;

  return d;
}

vec3 normal(in vec3 p) {
    vec2 e = vec2(1, -1) * EPSILON;
    return normalize(
      e.xyy * scene(p + e.xyy) +
      e.yyx * scene(p + e.yyx) +
      e.yxy * scene(p + e.yxy) +
      e.xxx * scene(p + e.xxx));
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos);
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd));
	vec3 cu = normalize(cross(cd, cr));
	return mat3(-cr, cu, -cd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy / iResolution.y;

    if (m == vec2(0.0)) m = vec2(0.5);

    vec3 col = vec3(0);
    vec3 lp = vec3(0);
    vec3 ro = vec3(0, 0, 1);

    float camRadius = 1.0;
    ro.yz = ro.yz * camRadius * rotmat2(mix(-PI/2., PI/2., m.y));
    ro.xz = ro.xz * rotmat2(mix(-PI, PI, m.x)) + vec2(lp.x, lp.z);

    vec3 rd = camera(ro, lp) * normalize(vec3(p, -1));

    float d = march(ro, rd);

    if (d > MAX_DIST) {
        col = COLOR_BACKGROUND;
    } else {
        vec3 p = ro + rd * d;
        vec3 norm = normal(p);

        vec3 lightPos = vec3(0, 2, 2);
        vec3 lightDir = normalize(lightPos - p) * 0.65;

        float diff = clamp(dot(norm, lightDir), 0.0, 1.0) * 0.5 + 0.5;

        vec3 rnd = 0.5 + 0.5 * cos(iTime + p.xyx + vec3(0, 2, 4));
        col = vec3(diff) + rnd;    
    }

    fragColor = vec4(col, 1.0);
}

// math
//// constants
#define PI 3.14159265359
#define cos30 cos(radians(30.0))
#define sqrt3 sqrt(3.0)
//// schwarz triangle
#define v1 vec2(0, 0)
#define v2 vec2(0, cos30)
#define v3 vec2(0.5, cos30)

// ray marching
#define MAX_ITER 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define PRECISION 0.001
#define EPSILON 0.0005
#define COLOR_BACKGROUND vec3(0, 0, 0)
#define COLOR_AMBIENT vec3(0.42, 0.20, 0.1);

//// tetrahedron
////// radius
#define TET_R 0.25
////// vertexes
#define TET_V vec4[] (               \
    vec4(+TET_R, +TET_R, +TET_R, 1), \
    vec4(-TET_R, +TET_R, -TET_R, 1), \
    vec4(+TET_R, -TET_R, -TET_R, 1), \
    vec4(-TET_R, +TET_R, -TET_R, 1), \
    vec4(-TET_R, -TET_R, +TET_R, 1), \
    vec4(+TET_R, -TET_R, -TET_R, 1)  \
)                                    \
////// faces
#define TET_F vec3[] ( \
    vec3(0, 1, 2),     \
    vec3(1, 4, 2),     \
    vec3(0, 2, 4),     \
    vec3(0, 4, 1)      \
)                    
////// transformation matrices
/*
M' = {{0.7071, 0.4082, 0.5774, 0.0833}, {-0.7071, 0.4082, 0.5774, 0.0833}, {0, 0.8165, -0.5774, -0.0833}, {0, 0, 0, 1}}
M' = {{0.7071, -0.4082, -0.5774, -0.0833}, {0, 0.8165, -0.5774, -0.0833}, {-0.7071, -0.4082, -0.5774, -0.0833}, {0, 0, 0, 1}}
M' = {{-0.7071, 0.4082, -0.5774, 0.0833}, {0, 0.8165, 0.5774, -0.0833}, {0.7071, 0.4082, -0.5774, 0.0833}, {0, 0, 0, 1}}
M' = {{0, 0.8165, 0.5774, -0.0833}, {0.7071, 0.4082, -0.5774, 0.0833}, {-0.7071, 0.4082, -0.5774, 0.0833}, {0, 0, 0, 1}}
*/
const mat4 M1 = mat4(0.7071, 0.4082, 0.5774, 0.0833, -0.7071, 0.4082, 0.5774, 0.0833, 0, 0.8165, -0.5774, -0.0833, 0, 0, 0, 1);
const mat4 M2 = mat4(0.7071, -0.4082, -0.5774, -0.0833, 0, 0.8165, -0.5774, -0.0833, -0.7071, -0.4082, -0.5774, -0.0833, 0, 0, 0, 1);
const mat4 M3 = mat4(-0.7071, 0.4082, -0.5774, 0.0833, 0, 0.8165, 0.5774, -0.0833, 0.7071, 0.4082, -0.5774, 0.0833, 0, 0, 0, 1);
const mat4 M4 = mat4(0, 0.8165, 0.5774, -0.0833, 0.7071, 0.4082, -0.5774, 0.0833, -0.7071, 0.4082, -0.5774, 0.0833, 0, 0, 0, 1);

mat2 rotmat2(float angle) {
    float c = cos(angle), s = sin(angle);
    return mat2(c, -s, s, c);
}

mat3 rotmat3(vec3 angle) {
    float sintht = sin(angle.x), sinpsi = sin(angle.y), sinphi = sin(angle.z);
    float costht = cos(angle.x), cospsi = cos(angle.y), cosphi = cos(angle.z);
    return mat3(
        costht * cospsi, sintht * cospsi, -sinpsi, 
        costht * sinpsi * sinphi - sintht * cosphi, sintht * sinpsi * sinphi + costht * cosphi, cospsi * sinphi,
        costht * sinpsi * cosphi + sintht * sinphi, sintht * sinpsi * cosphi - costht * sinphi, cospsi * cosphi
    );
}

vec2 intersection(vec2 p1, vec2 p2, vec2 p3, vec2 p4) {
    // http://paulbourke.net/geometry/pointlineplane/
    float d = (p4.y - p3.y) * (p2.x - p1.x) - (p4.x - p3.x) * (p2.y - p1.y);
    float ua = ((p4.x - p3.x) * (p1.y - p3.y) - (p4.y - p3.y) * (p1.x - p3.x)) / d;
    // float ub = ((p2.x - p1.x) * (p1.y - p3.y) - (p2.y - p1.y) * (p1.x - p3.x)) / d;
    return p1 + ua * (p2 - p1);
}

float opIntersection( float d1, float d2 ) { 
    // https://iquilezles.org/articles/distfunctions2d/
    return max(d1, d2); 
}

float opUnion(float d1, float d2) {
    // https://iquilezles.org/articles/distfunctions2d/
    return min(d1, d2); 
}

float opOnion(in float sdf, in float thickness) {
    // https://iquilezles.org/articles/distfunctions2d/
    return abs(sdf) - thickness;
}

vec2 opRep(in vec2 p, in vec2 c) {
    // https://iquilezles.org/articles/distfunctions2d/
    return mod(p + 0.5 * c, c) - 0.5 * c;
}

float opExtrusion(in float p, in float d, in float h)
{
    // modified: https://iquilezles.org/articles/distfunctions/
    vec2 w = vec2(d, abs(p) - h);
    return min(max(w.x, w.y), 0.0) + length(max(w, 0.0));
}

float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
    // https://iquilezles.org/articles/distfunctions2d/
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float sdCircle(vec2 p, float r) {
    // https://iquilezles.org/articles/distfunctions2d/
    return length(p) - r;
}

float sdTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2) {
    // https://iquilezles.org/articles/distfunctions2d/
    vec2 e0 = p1 - p0, e1 = p2 - p1, e2 = p0 - p2;
    vec2 t0 = p  - p0, t1 = p  - p1, t2 = p  - p2;
    vec2 pq0 = t0 - e0 * clamp(dot(t0, e0)/ dot(e0, e0), 0.0, 1.0);
    vec2 pq1 = t1 - e1 * clamp(dot(t1, e1)/ dot(e1, e1), 0.0, 1.0);
    vec2 pq2 = t2 - e2 * clamp(dot(t2, e2)/ dot(e2, e2), 0.0, 1.0);
    float s = sign(e0.x * e2.y - e0.y * e2.x );
    vec2 d = min(min(vec2(dot(pq0, pq0), s * (t0.x * e0.y - t0.y * e0.x)),
                     vec2(dot(pq1, pq1), s * (t1.x * e1.y - t1.y * e1.x))),
                     vec2(dot(pq2, pq2), s * (t2.x * e2.y - t2.y * e2.x)));
    return -sqrt(d.x) * sign(d.y);
}

float sdEquilateralTriangle(in vec2 p, in float r) {
    // https://iquilezles.org/articles/distfunctions2d/
    // r is half of the side
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r / k;
    if (p.x + k * p.y > 0.0) p = vec2(p.x - k * p.y, -k * p.x - p.y) / 2.0;
    p.x -= clamp(p.x, -2.0 * r, 0.0);
    return -length(p) * sign(p.y);
}

vec2 snub632() {
    // fermat point
    vec2 pf = intersection(vec2(-0.75, cos30 / 2.0), v3, vec2(1, 0), v2);
    // reflect over side (y-axis)
    vec2 q1 = vec2(-1.0 * pf.x, pf.y);
    // vec2 q2 = vec2(pf.x, pf.y + 2.0 * (cos30 - pf.y));
    // project to hypotenuse then double to obtain reflection
    vec2 q3 = dot(pf, v3) / dot(v3, v3) * v3;
    q3 = pf + 2.0 * (q3 - pf);
    // snub point is at half the hypotenuse of the new right triangle (Thales's theorem)
    return q1 + ((q3 - q1) / 2.0);
}

float sdSchwartz632Snub(vec2 p, float t) {
    // p the uv
    // t the thickness
    // take advantage of symmetry
    float a = atan(p.y, p.x);
    float M = floor(mod(degrees(a) - 30.0, 360.0) / 60.0) * 60.0;
    p = rotmat2(radians(M)) * p;
    // central point
    float d = sdCircle(p, t);
    // snub point
    vec2 g = snub632();
    // rotate
    vec2 G = rotmat2(radians(180.0)) * (g - v2) + v2;
    // d = opUnion(d, opOnion(sdTriangle(p, v1, v2, v3), t*0.25)); 
    d = opUnion(d, sdSegment(p, g, G) - t);
    // fix by https://www.shadertoy.com/user/mla
    d = opUnion(d, sdSegment(rotmat2(radians(-60.0)) * p, g, G) - t);
    d = opUnion(d, sdSegment(p, g, rotmat2(radians(-60.0)) * (G - g) + g) - t);
    d = opUnion(d, sdSegment(p, g, rotmat2(radians(+60.0)) * (G - g) + g) - t);
    d = opUnion(d, sdSegment(p, g, rotmat2(radians(+120.0)) * (G - g) + g) - t);
    d = opUnion(d, sdSegment(p, g, rotmat2(radians(+180.0)) * (G - g) + g) - t);
    return d;
}

float sdSchwartz632DualSnub(vec2 p, float t) {
    // p the uv
    // t the thickness
    // snub point
    vec2 g = snub632();
    // take advantage of symmetry
    float a = atan(p.y, p.x) - (atan(g.y, g.x) - radians(60.0));
    float M = floor(mod(degrees(a), 360.0) / 60.0) * 60.0;
    p = rotmat2(radians(M)) * p;
    // central point
    float d = sdCircle(p, t);
    float R3 = sqrt3 / 2.0 * length(g);
    float r3 = sqrt3 / 6.0 * length(g); 
    vec2 n0 = normalize(rotmat2(radians(+30.0)) * g);
    vec2 n1 = (R3 + r3) * n0;
    vec2 n2 = n1 + rotmat2(radians(-60.0)) * (2.0 * r3 * n0);
    vec2 n3 = n1 + rotmat2(radians(+60.0)) * (2.0 * r3 * n0);
    d = opUnion(d, sdSegment(p, v1, n1) - t);
    d = opUnion(d, sdSegment(p, n1, n2) - t);
    d = opUnion(d, sdSegment(p, n1, n3) - t);
    d = opUnion(d, sdSegment(p, n2, n1 + n2 + -n3) - t);
    d = opUnion(d, sdSegment(p, n3, n1 + n3 + -n2) - t);
    return d;
}

float sdSchwartz632(vec2 p, vec2 g, float m, float w, float t) {
    // p the uv
    // g the generator point
    // m the mirror toggle
    // w the wall toggle
    // t the thickness
    // snub case
    if (m == 8.0) {
        return sdSchwartz632Snub(p, t);
    }
    // snub case
    else if (m == 9.0) {
        return sdSchwartz632DualSnub(p, t);
    }
    p = abs(p);
    if (p.y < 2.0 * cos30 * p.x) {
        p = rotmat2(radians(-60.0)) * p;
        p = abs(p);
    }
    float d = sdCircle(p, t); 
    // d = opUnion(d, opOnion(sdTriangle(p, v1, v2, v3), t*0.25)); 
    d = opUnion(d, sdCircle(p - g, t));
    d = bool(mod(m, 2.0)) ? opUnion(d, sdSegment(p, g, vec2(0, g.y)) - t) : d;
    vec2 v = v3 - v1;
    d = bool(mod(floor(m / 2.0), 2.0)) ? opUnion(d, sdSegment(p, g, dot(g, v) / dot(v, v) * v) - t) : d;
    d = bool(mod(floor(m / 4.0), 2.0)) ? opUnion(d, sdSegment(p, g, vec2(g.x, v2.y)) - t) : d;
    d = bool(mod(w, 2.0)) ? opUnion(d, sdSegment(p, v1, v2) - t) : d;
    d = bool(mod(floor(w / 2.0), 2.0)) ? opUnion(d, sdSegment(p, v1, v3) - t) : d;
    d = bool(mod(floor(w / 4.0), 2.0)) ? opUnion(d, sdSegment(p, v2, v3) - t) : d;
    return d;
}

float sdSchwartz632Plane(vec2 p, vec2 g, float m, float w, float t) {
    // p the uv
    // g the generator point
    // m the mirror toggle
    // w the wall toggle
    // t the thickness
    vec2 v = vec2(3.0, 2.0 * cos30);
    vec2 p1 = opRep(p, v);
    vec2 p2 = opRep(p + v / 2.0, v);
    return opUnion(
        sdSchwartz632(p1, g, m, w, t),
        sdSchwartz632(p2, g, m, w, t)
    );
}

float sdCasparKlugFacet(vec2 p, float h, float k, vec2 g, float m, float w, float t) {
    // @p vec2 the uv
    // @h float the h-parameter (steps on plane)
    // @k float the k-parameter (steps on plane after 60-degree turn)
    // @g vec2 the generator point for Wythoff construction
    // @m float the bits representing the activated mirrors for Wythoff construction
    // @w float the bits representing the fundamental triangle wall display
    // @t float the edge thickness
    mat2 b = mat2(1.5, cos30, 0.0, 2.0 * cos30);
    // vec2 t1 = vec2(0);
    vec2 t2 = b * vec2(h, k);
    vec2 t3 = rotmat2(radians(-60.0)) * t2;

    float s = length(t2); // side
    // vec2 c = (t1 + t2 + t3) / 3.0; // centroid
    vec2 c = (t2 + t3) / 3.0; // centroid

    float d = sdSchwartz632Plane((rotmat2(-atan(t2.y, t2.x)) * p) + c, g, m, w, t);
    // d = opUnion(d, opOnion(sdCircle(p, 1.5 * t), t)); 
    // d = opUnion(d, opOnion(sdEquilateralTriangle(p, s / 2.0), t));
    d = opIntersection(d, sdEquilateralTriangle(p, s / 2.0));

    return d;
}
