// Fluid Flood Flavor
//
// variation of "Fire Fighter Fever" https://shadertoy.com/view/msf3WH

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // normal
    float rng = hash13(vec3(fragCoord, iFrame));
    vec3 unit = vec3(vec2(.001), 0.);
    vec3 normal = normalize(vec3(T(uv-unit.xz)-T(uv+unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 .0001));

    // distort
    fragColor = texture(iChannel1, uv+.1*normal.xy);

    // debug art
    if (iMouse.z > 0. && iMouse.x/R.x < .2)
    {
        vec4 data = texture(iChannel0, uv);
        if (uv.x > .66) fragColor = vec4(normal*.5+.5, 1);
        else if (uv.x > .33) fragColor = vec4(vec3(sin(data.r*6.28*2.)*.5+.5), 1);
        else fragColor = vec4(data.yz*.5+.5,.5, 1);
    }
}

#define R iResolution.xy
#define ss(a,b,t) smoothstep(a,b,t)
#define N normalize
#define T(uv) texture(iChannel0, uv).r

// Dave Hoskins https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// Martijn Steinrucken youtube.com/watch?v=b0AayhCO7s8
float gyroid (vec3 seed)
{
    return dot(sin(seed),cos(seed.yzx));
}

/////////// spicy noise
float fbm (vec3 seed)
{
    // thebookofshaders.com/13
    float result = 0.;
    float a = .5;
    for (int i = 0; i < 4; ++i)
    {
        // distort
        seed += result / 2.;

        // animate
        seed.y -= .1*iTime/a;

        // accumulate
        result += gyroid(seed/a)*a;

        // granule
        a /= 3.;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    /////////// coordinates
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.*fragCoord-R)/R.y;

    // salt
    float rng = hash13(vec3(fragCoord, iFrame));

    // noise
    vec3 seed = vec3(p * vec2(1,.2), length(p) + iTime) * 2.;
    float noise = fbm(seed);
    float a = noise * 3.14;

    // normal
    vec3 unit = vec3(vec2(.005), 0.);
    vec3 normal = normalize(vec3(T(uv-unit.xz)-T(uv+unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 unit.y));

    // mouse
    vec2 mouse = iMouse.xy/R;
    float clic = step(0., iMouse.z);


    ////////// shape
    float shape = 1.;

    // bottom line
    shape *= ss(.01,.0,1.-uv.y);

    // wave
    shape *= ss(.5,.6,sin(iTime*3.));

    // salt
    shape *= pow(rng, 4.);



    ////////// forces field
    vec2 offset = vec2(0);

    // turbulence                     
    offset -= vec2(cos(a),sin(a)) * fbm(seed+.195);

    // slope
    offset -= normal.xy * .5;

    // gravity
    offset += vec2(0,1);

    // obstacle
    offset *= texture(iChannel1, uv).r;

    // mouse
    vec2 velocity = vec2(0);
    p -= (2.*iMouse.xy-R)/R.y;
    float mouseArea = ss(.3,.0,length(p)-.1);
    offset -= clic * normalize(p) * mouseArea;
    velocity += (texture(iChannel0, vec2(0)).yz - mouse);
    if (length(velocity) > .001) velocity = clic * normalize(velocity) * mouseArea;

    // inertia
    velocity = clamp(texture(iChannel0, uv).yz * .99 + velocity * .5,-1.,1.);

    // inertia
    offset += velocity;

    // apply
    uv += .01 * offset;



    ////////// frame buffer
    vec4 frame = texture(iChannel0, uv);

    // fade out
    float fade = iTimeDelta*.2;
    shape = max(shape, frame.r - fade);

    // result
    shape = clamp(shape, 0., 1.);
    fragColor = vec4(shape, velocity, 1);

    // previous mouse
    if (fragCoord.x < 1. && fragCoord.y < 1.) fragColor = vec4(0,mouse,1);
}
