/* Creative Commons Licence Attribution-NonCommercial-ShareAlike 
   phreax 2022
*/

#define PI 3.141592
#define TAU 2.*PI
#define hue(v) ( .6 + .6 * cos( 6.3*(v) + vec3(0,23,21) ) )
#define SIN(x) (sin(x)*.5+.5)
#define BEND .8     
#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define COUNT 100.
#define DISTORT 2.
#define COLOR 1.
#define SPIRAL 3.
#define S(a, b, x) smoothstep(a, b, x)

//#define WATERCOLOR
//#define TUNNEL
#define FFT 0.


float tt, bass;


float bassFFT() {
    int bandLimit = 5;

    float avg = 0.;
    for(int i=0; i<bandLimit; i++) {
        avg += texelFetch(iChannel1, ivec2(i, 0), 0).x;
    }
    return avg/float(bandLimit);
}

// zucconis spectral palette https://www.alanzucconi.com/2017/07/15/improving-the-rainbow-2/
vec3 bump3y (vec3 x, vec3 yoffset)
{
    vec3 y = 1. - x * x;
    y = clamp((y-yoffset), vec3(0), vec3(1));
    return y;
}

vec3 spectral_zucconi6(float x) {
    x = fract(x);
    const vec3 c1 = vec3(3.54585104, 2.93225262, 2.41593945);
    const vec3 x1 = vec3(0.69549072, 0.49228336, 0.27699880);
    const vec3 y1 = vec3(0.02312639, 0.15225084, 0.52607955);
    const vec3 c2 = vec3(3.90307140, 3.21182957, 3.96587128);
    const vec3 x2 = vec3(0.11748627, 0.86755042, 0.66077860);
    const vec3 y2 = vec3(0.84897130, 0.88445281, 0.73949448);
    return bump3y(c1 * (x - x1), y1) + bump3y(c2 * (x - x2), y2) ;
}


float triangle(vec2 uv, float w, float blur) {   
  int N = 3;

  // Angle and radius from the current pixel
  float a = atan(uv.x,uv.y)+PI;
  float r = TAU/float(N);

  // Shaping function that modulate the distance
  float d = 1.-cos(floor(.5+a/r)*r-a)*length(uv);
  return smoothstep(blur, .0, abs(d)-w)*(0.3/blur);
}

vec3 spiral(vec2 uv, float z) {  

    float spiralAmp = (3.*SIN(.5*PI+iMouse.y/360.*2.*PI));

    uv *= rot((z*TAU-.2*tt)*spiralAmp);

    #ifdef TUNNEL
    uv.y += 1.7*sin(.4*z+2.4*tt);
    uv.x -= .9*sin(.3*z+1.8*tt);
    #endif
    float soundMod = bass*FFT*(3.*sqrt(z)+.2);
    float dist = DISTORT * sin(.25*PI+iMouse.x/640.*2.*PI+soundMod);

    uv += dist*sin(vec2(5)*uv.yx+.2*tt);//.3*pow(sin(.2*tt+z+uv.x), 2.);

    float blur = exp(-19.*z);
    float luma = exp(-19.5*z);
    vec3 col = spectral_zucconi6(fract(2.*z+.2*tt+uv.x*.2+bass*sqrt(z)*2.*FFT))*.8;



    float size = .05;
    return triangle(uv, size, blur)*luma*col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    vec4 tex = texture(iChannel0, fragCoord/iResolution.xy);
    if (tex.a != iResolution.x) tex = vec4(0);
    tt = iTime*.2;

    bass = bassFFT();

    uv *= 100.;

    float s = 1./COUNT; 

    for(float i=0.; i<1.; i+=s) {   
        float z = fract(i-.1*tt);
        float fade = smoothstep(1., .9, z);
        vec2 UV = uv;
        col += spiral(UV*z, z)*fade;

    }
    col = pow(col, vec3(0.5));
    #ifdef WATERCOLOR
    col = 1.05-col;
    col = pow(col, vec3(2.2));
    col = clamp(col, vec3(0), vec3(1));
    #endif

    col = mix(col, tex.rgb, 0.98);

    // Output to screen
    fragColor = vec4(col, iResolution.x);
}
