// https://www.desmos.com/calculator/njkwv6z5hi
#define PI 3.1415926536
#define SQRT_PI 1.7724538509

#define sqr(x) ((x) * (x))
#define erf(x) (sign(x) * sqrt(1.0 - exp2(-1.787776 * (x) * (x))))
vec3 gpatch(in vec2 p, in vec2 w, in vec3 v1, in vec3 v2, in vec3 v3, in vec3 v4) {
    vec3 c4 = v1;
    vec3 c3 = v3 - v1;
    vec3 c2 = v2 - v1;
    vec3 c1 = v4 - v3 - c2;

    vec2 k1 = (exp(-sqr(p / w)) - exp(-sqr((1.0 - p) / w))) * w * w / 2.0;
    vec2 k2 = (erf(p / w) + erf((1.0 - p) / w)) * w * SQRT_PI / 2.0;

    return (((k1.y + p.y * k2.y) * c1 + c2 * k2.y) * (k1.x + p.x * k2.x) + ((k1.y + p.y * k2.y) * c3 + c4 * k2.y) * k2.x) / (PI * w.x * w.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - vec2(0.5 * iResolution.x, 0.0)) / iResolution.y + vec2(0.5, 0.0);
    vec3 color = vec3(0.0);

    vec3 v3 = vec3(1, 0, 1), v4 = vec3(0, 1, 1);
    vec3 v1 = vec3(1, 0, 0), v2 = vec3(0, 1, 0);

    float filterWidth = 0.05 + 0.05 * sin(iTime) + 0.001;
    fragColor = vec4(gpatch(uv, vec2(filterWidth), v1, v2, v3, v4), 1.0);
}
