mat2 rotate2d(float angle){
   return mat2(
       cos(angle),-sin(angle),
       sin(angle),cos(angle)
   );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //f = texelFetch(iChannel0, ivec2(g),0);
    vec2 uv = fragCoord/iResolution.xy-0.5;
	uv.x *= iResolution.x/iResolution.y;
    float a = 3.1415/6.;
    float a_cos = cos(a);
    float a_sin = sin(a);
    mat2 rot = mat2(a_cos, -a_sin, a_sin, a_cos); 
    for (float i=0.0; i<3.; i++ )
        uv = abs(uv*rot);  
    for (int i = 0; i <11; i++) {
        float a = atan(uv.y, uv.x)*(0.5 + cos(iTime*0.2)*uv.y);
        a_sin = sin(a);
        a_cos = cos(a);
        uv = uv * mat2(a_cos, -a_sin, a_sin, a_cos);
        uv.y = abs(uv.y) + sin(iTime)*0.4 *uv.x;
        uv.x += 0.2+ sin(iTime)*0.02 + 0.02;
    }
    uv /= 3.1;

    // Normalized pixel coordinates (from 0 to 1)

    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    uv *= rotate2d(iTime)*12.0; // Scale the coordinate system by 10
    vec2 int_pos = floor(uv);  // get the integer coords
    vec2 float_pos = fract(uv);  // get the fractional coords

    // rand for cell
    vec3 color = a+vec3(uv.x-uv.y);

    // color
    color =vec3(0);
    color *= vec3(11.0, 11.0, 11.0);
    // Time varying pixel color
    vec3 col = sin(uv.xyx+vec3(cos(1.0/iTime),sin(1.0/iTime),(int_pos/iTime).x*cos(1.0/iTime)));
        fragColor = vec4(
        smoothstep((int_pos/iTime).x, (int_pos/iTime).y, fract(abs(uv.y-(float_pos/iTime).x)+iTime)),
        smoothstep((int_pos/iTime).x, (int_pos/iTime).y, fract(abs(uv.y-(float_pos/iTime).x)+iTime)),
        smoothstep((int_pos/iTime).x, (int_pos/iTime).y, fract(abs(uv.y-(float_pos/iTime).x)+iTime)),
    1.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
