float circleStep(float thickness, float inner, float outer, float value) {
    return smoothstep(inner, inner+thickness, value) - smoothstep(outer-thickness,outer,value);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    clamp(fragCoord, 0.0,iResolution.y);
    vec2 st = fragCoord/iResolution.y;
    float time = mod(iTime,45.0)+0.05;
    float pct = 0.0;
    float pct2 = 0.0;
    //Circle using distance
    pct = distance(st,vec2(0.2,0.2));
    pct2 = distance(st,vec2(0.8,0.5));
    float select = floor(pct*time);
    float select2 = floor(pct*time*1.2);
    pct = fract(pct*time);
    pct2 = fract(pct2*time*1.2);
    vec3 color = vec3(1.0);
    //Weird Color effect
    //color = vec3(1.0-mod(select,3.0),1.0-mod(st.x*select+1.0*iTime,3.0),1.0-mod(select*st.y+2.0*iTime,3.0));
    //RGB Color
    //color = vec3(1.0-mod(select,3.0),1.0-mod(select+1.0,3.0),1.0-mod(select+2.0,3.0));
    //CMY Color
    color = vec3(mod(select,3.0),mod(select+1.0,3.0),mod(select+2.0,3.0));
    vec3 color2 = vec3(1.0-mod(select2,3.0),1.0-mod(select2+1.0,3.0),1.0-mod(select2+2.0,3.0));
    //pct = smoothstep(0.30,0.32,pct)-smoothstep(0.38,0.40,pct);
    pct = circleStep(0.02, 0.30,0.40,pct);
    pct2 = circleStep(0.07,0.25,0.40,pct2);
    //Circle using length
    //vec2 toCenter = vec2(0.5)-st;
    //pct = length(toCenter);

    //Circle using square root
    //pct = sqrt((st.x-0.5)*(st.x-0.5)+(st.y-0.5)*(st.y-0.5));
    vec3 col = pct*color+pct2*color2;
    // Output to screen
    fragColor = vec4(col,1.0);
}
