float f_n_mixed(float n1, float n2, float n_intensity){
//https://thebookofshaders.com/glossary/?search=mix
 float n_mixed = n1*(1.-n_intensity)+n2*n_intensity;
 return n_mixed;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_fc_nor = fragCoord/iResolution.xy;

    vec4[] a_o = vec4[] (
        // red to black
        vec4(
            f_n_mixed(1., 0., o_fc_nor.x),
            0.,
            0.,
            0.
        ), 
        // green to black
        vec4(
            0.,
            f_n_mixed(1., 0., o_fc_nor.x),
            0.,
            0.
        ), 
        // blue to black 
        vec4(
            0.,
            0.,
            f_n_mixed(1., 0., o_fc_nor.x),
            0.
        ), 
        // from red to blue 
        vec4(
            f_n_mixed(1., 0., o_fc_nor.x),
            0.,
            f_n_mixed(0., 1., o_fc_nor.x),
            0.
        ), 
        // red to blue different 
        vec4(
            f_n_mixed(0., 1., 1.-o_fc_nor.x),
            0.,
            f_n_mixed(0., 1., o_fc_nor.x),
            0.
        ),
        // from blue to red 
        vec4(
            f_n_mixed(0., 1., o_fc_nor.x),
            0.,
            f_n_mixed(1., 0., o_fc_nor.x),
            0.
        ), 
        // from red to black twice
        vec4(
            f_n_mixed(1., 0., fract(o_fc_nor.x*2.)),
            0., 
            0.,
            0.
        ), 
        // from red to black twice
        vec4(
            f_n_mixed(1., 0., fract(o_fc_nor.x*2.)),
            0., 
            0.,
            0.
        ), 
        // from red to black to red
        vec4(
            f_n_mixed(1., 0., 1.-(abs(o_fc_nor.x-0.5)*2.)),
            0., 
            0.,
            0.
        )
    );
    float n_index = (o_fc_nor.y/(1./ float(a_o.length())));
    float n_index_flr = floor(n_index);

    vec4 o = a_o[int(n_index_flr)];
    float n_index_nor = (n_index_flr/float(a_o.length()));

    float n_d_line = fract(n_index);
    n_d_line = smoothstep(0.05, 0.075, n_d_line);
    float n_d_line2 = float(n_d_line < 0.01);

    fragColor = o*n_d_line+n_d_line2;
    //fragColor = sqrt(fragColor);

}
