void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    vec2 uv = p * 2.0;

    float time = iTime * 0.5;

    // Create tunnel effect
    float tunnelDepth = 5.0;
    vec3 color = vec3(0.1);
    vec2 tunnelCenter = vec2(sin(time), cos(time)) * 0.5;
    float distanceToCenter = length(uv - tunnelCenter) * 2.0;
    float tunnel = mod(distanceToCenter - time * 3.0, tunnelDepth);
    color += vec3(0.5 + 0.5 * cos(tunnel * 3.14159));

    // Add dynamic patterns
    float pattern = cos(uv.x * 10.0 + time) * sin(uv.y * 20.0 + time);
    color += vec3(0.2 * pattern);

    // Apply color palette
    vec3 palette[5] = vec3[5](
        vec3(0.988, 0.325, 0.369),
        vec3(0.992, 0.671, 0.267),
        vec3(0.976, 0.831, 0.329),
        vec3(0.263, 0.792, 0.698),
        vec3(0.239, 0.345, 0.686)
    );
    int paletteIndex = int(mod(time * 2.0, 5.0));
    color *= palette[paletteIndex];

    fragColor = vec4(color, 1.0);
}
