renderpass.code:
// variant of https://shadertoy.com/view/dtc3WM
// variant of https://shadertoy.com/view/ml3GWN
// variant of https://shadertoy.com/view/mtc3D4
// variant of https://shadertoy.com/view/ct33WN

void mainImage( out vec4 O, vec2 u )
{
 // float h = 450.; // R.y;
    vec2 R = iResolution.xy,
         U = 5.*( 2.*u - R ) / R.y,                                    // global normalize coords
         I = floor(U)+.5,                                              // disc Id
         F = fract(U)-.5,                                              // local disc coords
         H = fract(1e4*sin(I*mat2(R-17.,R+71.)));                      // 2 random values 

    float p = 15./R.y, l = length(F), L = length(U), i = length(I);
    O = mix( vec4(0,.5,0,1),                                            // bg color
             vec4(.5+.5* sin( l > .4 - 2.*p* abs(dot(U,F))/L/l ?  3.*l - sign(L-i)-  - 6.28*H.y -20.*iTime* sign(H.x-.5)  : 0. )), // margin phase
             smoothstep(0.,-p, l-.4) );                                // draw disk
}
