#define UV_ORIGIN 0.5
#define ZOOM 1.0
#define SPEED 10.0

const float PI = 3.14;

// rotation function
mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 0.8;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // reset uv to 0.5 to get center and normalize
    uv = (uv - 0.5) * 2.0;
    uv.x *= iResolution.x/iResolution.y;

    // radian based rotation matrix fo zoom
    uv *= rotationMatrix( SPEED * iTime ) * ZOOM;

    float d = length(uv);

    // modulate smoothstep to get trippy effect
    float c = smoothstep(r+cos(iTime),r-sin(iTime),d);


    // Output to screen
    //fragColor = vec4(c+cos(c+iTime)/uv.y, c/sin(uv.y+iTime)-0.5,c-sin(uv.y-iTime)/uv.y, 0.5);
    fragColor = vec4(c-cos(c+iTime)*uv.x-0.5, c-c/sin(uv.x+iTime)-0.5, c,sin(uv.x));

}
