void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy / iResolution.y) * 2.0 - 1.0;
    p.x-=1.5;
    p *= 1.0 + 0.5 * sin(pow(2.0,-0.5)*iTime);
    vec2 c = p;
    c.x += cos(.3*iTime+1.0) * .8;
    c.y += sin(1.2*iTime-1.5) * .5;
    float i;
    for (i = 0.0; i < 1.0; i += 0.01)
    {
        float x = c.x;
        float y = c.y;
        float x2 = x*x;
        float y2 = y*y;
        c.x = x2 - y2 + p.x;
        c.y = 2.0*x*y + p.y;
        if (x2 + y2 > 8.0)
            break;
    }
    vec3 color = vec3(c.x*3.1-c.y*.4,c.x*.5+c.y*2.7,i*15.0);
    fragColor = vec4(color, .5);
}
