//generic noise function yoinked from Patricio Gonzales Vivo
float rand(float n){return fract(sin(n) * 43758.5453123);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    //creates a 2d sin function as coordinates that expands and contracts over time
    vec2 sinUV;
    sinUV.x = sin(4.0 * uv.x * (0.6 + 0.3 * sin(0.7 * iTime)));
    sinUV.y = sin(4.0 * uv.y * (0.6 + 0.3 * sin(0.7 * iTime)));
    float dist = length(sinUV);


    float f = 3.0;
    float speed = 3.0;
    float driftA = 0.3;
    float driftF = 0.7;

    //phase dispersion between the color channels
    float disp = 0.65;

    vec3 color = vec3(sin(-f * dist + speed * iTime + disp), 
                      sin(-f * dist + speed * iTime + driftA * sin(driftF * iTime)), 
                      sin(-f * dist + speed * iTime - disp));

    //add concentric rings that move inward 
    color *= cos(length(5.0 * uv) + 2.0 * iTime);

    //add a slight vignette
    color *= (0.5 + 0.7 * cos(length(uv)));

    //add noise
    float noise = rand(0.1 * rand(uv.x * uv.y) + 0.000007 * iTime);
    color += vec3(noise, noise, noise) * 0.5;

    fragColor = vec4(color, 1.0);
}
