void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //needed for anti aliasing ?
    float n_ratio_1to_iresx = 1./ iResolution.x;

    vec2 o_trn_fc_nor = fragCoord.xy / iResolution.xy;
    vec2 o_trn_mo_nor = iMouse.xy / iResolution.xy;


    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    vec2 o_trn_mo_nor_offset = (o_trn_mo_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);

    float n_d = length(o_trn_fc_nor_offset);
    float n_fuzz, n_min, n_d_smstp_absolute, n_d_smstp_relative;
    // if we use the ratio of 1. to the resolution
    // as a factor , we can keep the exact size when using smoothstep
    n_fuzz = n_ratio_1to_iresx*100.;
    n_min = n_ratio_1to_iresx*100.;
    n_d_smstp_absolute = smoothstep(n_min, n_min+n_fuzz, n_d);

    // otherwise 
    n_fuzz = 0.5;
    n_min = 0.1;
    n_d_smstp_relative = smoothstep(n_min, n_min+n_fuzz, n_d);
    fragColor = vec4(n_d_smstp_relative);

    if(o_trn_fc_nor_offset.x>0.){
        fragColor = vec4(n_d_smstp_absolute);

    }
}
