float f_n_dist_line_byiq(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_fc = (fragCoord.xy - iResolution.xy*.5) / iResolution.y;

    float n_radius;

    vec4 o_col = vec4(0.);
    vec2 o_p1 = vec2(-0.25, .33);
    vec2 o_p2 = vec2(0.25,  .33);
    vec2 o_p3 = vec2(0.0, -0.0);
    //test 2
    float n_thi = 0.01;
    float aa = 1./iResolution.x;;

    float n_aa = aa * 55.;
    float n = length(o_fc-o_p1);
    n = smoothstep(n_thi+n_aa, n_thi,n);
    o_col+=n;

    n = f_n_dist_line_byiq(o_fc, o_p1, o_p2);
    n = smoothstep(n_thi+n_aa, n_thi,n);
    o_col += n*vec4(0.23, 0.1, 0.8,1.);

    n = f_n_dist_line_byiq(o_fc, o_p2, o_p3);
    n = smoothstep(n_thi+n_aa, n_thi,n);
    o_col += n*vec4(0.78, 0.5, 0.3,1.);;

    n = f_n_dist_line_byiq(o_fc, o_p1, o_p3);
    n = smoothstep(n_thi+n_aa, n_thi,n);
    o_col += n*vec4(0.28, 0.78, 0.8,1.);;


    n_radius = 0.3;

    n = length(o_fc-o_p1);
    n_aa = aa * 25.;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);


    n = length(o_fc-o_p2);
    n_aa = aa * 25.;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);

    n = length(o_fc-o_p3);
    n_aa = aa * 25.;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);


    vec2 o_pmiddle = ((o_p2-o_p3)*.5)+o_p3;

    n = length(o_fc-o_pmiddle);
    n_aa = aa * 25.;
    n_radius = .02;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);



    float n_tau = 6.2831;
    float n_ang = atan((o_p2-o_p3).x, (o_p2-o_p3).y);
    n_ang += n_tau / 4.;
    vec2 o_lot = vec2(
        sin(n_ang)*0.25,
        cos(n_ang)*0.25
    );
    vec2 o_pn = o_lot+o_pmiddle;
        n = length(o_fc-o_pn);
    n_aa = aa * 25.;
    n_radius = .02;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);


    vec2 o_lot2 = vec2(
        sin(n_ang+n_tau*.5)*0.25,
        cos(n_ang+n_tau*.5)*0.25
    );
    vec2 o_pn2 = o_lot2+o_pmiddle;

    n = length(o_fc-o_pn2);
    n_aa = aa * 25.;
    n_radius = .02;
    n_thi = 0.001;
    n = smoothstep(n_thi+n_aa, n_thi,abs(n-n_radius));
    o_col += n*vec4(0.28, 0.78, 0.8,1.);

    n = f_n_dist_line_byiq(o_fc, o_pn, o_pn2);
    n = smoothstep(n_thi+n_aa, n_thi,n);
    o_col += n*vec4(0.28, 0.78, 0.8,1.);;


    fragColor = o_col;
}
