const vec2 a = vec2(0.3, 0.3);
const vec2 b = vec2(0.7, 0.4);
const vec2 c = vec2(0.5, 0.7);

float area(vec2 a, vec2 b, vec2 c)
{
    return cross(vec3(b - a, 0.0), vec3(c - a, 0.0)).z / 2.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord / iResolution.xy;
    //vec2 b = iMouse.xy;
    float totalArea = area(a, b, c);
    fragColor = vec4(area(p, b, c) / totalArea, area(a, p, c) / totalArea,
                     area(a, b, p) / totalArea, 1.0);
    if (sign(area(p, b, c)) == sign(area(a, p, c)) &&
        sign(area(a, p, c)) == sign(area(a, b, p))) {
        fragColor =
            mix(fragColor, vec4(1, 1, 1, 1), (sin(iTime * 3.0) + 1.0) / 4.0);
    }
    if (iMouse.z > 0.0) {
        //  fragColor =
        //         mix(fragColor, vec4(1, 1, 0, 1), smoothstep(1.0, 0.0,
        //         area(p, b, c) / totalArea+area(a, p, c) / totalArea+area(a, b, p) / totalArea));
        fragColor = vec4(1.0 / ((abs(area(p, b, c)) + abs(area(a, p, c)) +
                                 abs(area(a, b, p))) /
                                totalArea),
                         0.0, 0.0, 1.0);
    }
}
