// "tetragonal hexahedron slides"
//
// created by Colling Patrik (cyperus) in 2023
//
//
// References & Inspiration:
// - "Schwarz-Christoffel Mapping" by mla
//   https://www.shadertoy.com/view/tsfyRj
//
// - "Year of Truchets" #058 by byt3_m3chanic
//   https://www.shadertoy.com/view/msVfzR
//
// - conformal mapping with confmap.m by Trefethen
//   https://people.maths.ox.ac.uk/trefethen/lightning.html
//
// - signed distance functions & operations by iq
//   https://iquilezles.org/articles/distfunctions/
//
// - quick guide for shadertoy by FabriceNeyret2
//   https://shadertoyunofficial.wordpress.com/
//
////////////////////////////////////////////////////////////////////////////////
// ray casting bounding shape
const float CAM_DIST = 5.; 
const float CAM_FLE  = 2.;
const float BB_DIST  = 5.;
const float RAY_MIN_DIST = 0.;
const float RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;

const int   MAX_MARCHING_STEPS = 310;
const float RAY_STEP_FACTOR = 0.06676;
const float RAY_PRECISION = 1.;

// data from sdf
vec3 mat_to = vec3(0);
vec4 mat_tc = vec4(0);

float map(in vec3 p) {
    float rxy = 0., au = 0.0;
    vec2 z = vec2(0.);

    /// platonic sphere
    float ps_id = 0., sr = 0.;
    // normalize spherical radius
    vec3 pn = normalize(p);

    // projection from n cells to 1 cell.
    pn = hexa_cells(pn, ps_id, sr);

    // inverse stereographic projection
    z = pn.xy/(1.-pn.z);
    z = cHexaPolyArc2disk(z);

    // r-polygon-distribution
    float e_y = 0.4 + 0.1 *cos(4.*atan(z.y, z.x));
    float en = 1.-e_y *smoothstep(1.,5., abs(12.));        
    float r = length(z); vec2 zn = z/r;
    z = dist(length(z), en) * z/r;     

    // stereograpic projection
    float zz = dot(z,z); pn = vec3(2.*z.x, 2.*z.y, -1.+ zz); pn /= 1.+zz;

    // unnormalize spherical radius
    p = length(p) * pn;

    /// regular polygon tiles
    vec3 rpt_id = vec3(1.);  float rpt_sr;

    z = recgrid2rec(p.xy, rpt_id.xy, rpt_sr);

    // Schwarz-Christoffle
    z = inversesc(z,4);
    p.xy = z;

    /// (torus,sphere,2spheres)  
    rxy = length(p.xy); au = atan(p.y, p.x);
    z = ccos(PI*vec2(rxy,p.z))/0.5; z.x -= -1.28;   

    p = vec3(log(length(z)), 0.5*au/PI, 0.5*atan(z.y,z.x)/PI); // r,u,v
    vec2 to_uv = p.yz; float to_d = log(length(z)+0.8);   

    // truchet grid
    float TC3D_nu = 16.;
    float TC3D_nv = 8.;
    float TC3D_nr = 1.;
    p *= vec3(TC3D_nr,TC3D_nu,TC3D_nv); // r,u,v
    p.yzx = p.xyz; 

    ivec3 l0 = -ivec3(TC3D_nv/2.,0,TC3D_nu/2.);
    ivec3 l1 = ivec3(TC3D_nv/2.,TC3D_nr,TC3D_nu/2.)-ivec3(1.);
    vec3 tc_id = floor(p);
    tc_id = clamp(tc_id, vec3(l0), vec3(l1));
    vec3 q = p - tc_id -0.5;

    // random
    float rnd = hash21(tc_id.xz);  

    // flip cell direction randomly
    if(rnd<.5) q.x = -q.x;

    // sdf
    float d;
    if(fract(rnd*77.57+iTime/10.)>.75) {
       // offset-xz circle, id diagonal sub domain
       vec3 q2 =  length(q.xz-.5)<length(q.xz+.5)
                ? vec3(q.xz-.5,0.)  
                : vec3(q.xz+.5,1.);
       d = sdTorus( vec3(q2.x, q.y, q2.y) , vec2(0.5,0.0625) );
    }
    else {
       d = opSmoothUnion( sdCylinder( q.zxy, 0.0625 ), sdCylinder( q.xzy,0.0625 ), 0.05 );
    }

    /// return
    mat_to = vec3(to_uv,to_d); // torus: surface uv, distance function
    mat_tc = vec4(q,rnd);      // truchet: local cell coords, random cell id
    return min(d,to_d);
}

// intersect
float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0;

    // bounding shape BB
    vec2 dis = isphere( ro, rd , abs(BB_DIST));

    if( dis.y<0.0 ) return -1.0;

    dis.x = max( dis.x, RAY_MIN_DIST );
    dis.y = min( dis.y, RAY_MAX_DIST );

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR;
    float t = dis.x;
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) {
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t;
        float h = map( pos);
        if( t>dis.y || h<th ) break;
        t += h*fh;
    }
    if( t<dis.y ) {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) { 
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    p.yz = crot(p.yz,-2.*PI*1.*time/12.);
    p.zx = crot(p.zx,-2.*PI*2.*time/12.);
	return p.xyz;
}

// textures
vec4 ribbon(in float uv_x, in float r, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 2./6.)-1.);
    float px = fwidth(sdf); float a = smoothstep(px+0.1,-px-0.1,sdf+0.2);    
    vec4 C = r<0.5 ? vec4( h2rgb(sdf -2./6.),a ) : vec4(0.);
    return C;
}

vec4 truchet3D(in vec3 p, in float rnd ,in float time) {
    float sdf = sdBox( p, vec3(0.5*0.9));
    float px = fwidth(sdf); float a = smoothstep(px,-px,sdf);
    return mix(vec4(1.), vec4(h2rgb( fract(abs(rnd)*22.93 + time)),1.), a);
}

//
void mainImage(out vec4 O, in vec2 fragCoord) {   
    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {       
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,iTime/60.);
             rd = normalize(transform(rd,iTime/60.));

        // distance hit ray surface
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px );

        vec3 sky_col = rd*.2+.8;
        // background
        vec4 C = vec4(sky_col,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            // draw texture
            O = vec4(0);

            if(O.a<1.) {             
                C = ribbon( 12.* mat_to.x, mat_to.z, 6.*iTime/60.); C.rgb *= 0.7;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            if(O.a<1.) {
                C = vec4(vec3(n)*0.5,0.2);
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            if(O.a<1.) {            
                C = truchet3D(mat_tc.xyz, mat_tc.w, iTime/60.); C.rgb *= 0.5;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            C = O;

            // lighting
            vec3  came_li_o   = transform(vec3(-10.,10.,-10.),iTime/60.); // origine
            vec3  came_li_dir = normalize(p - came_li_o);             // direction
            vec3  came_li_col = vec3(1.,1.,.5);                       // color
            float came_li_shi = 10.;                                  // shininess            

            float cosa = 1.;
            O.rgb += 0.1 * cosa * C.rgb * sky_col;           // amb
            cosa = clamp(dot(-came_li_dir, n), 0., 1.);
            O.rgb += 0.5 * cosa * C.rgb * came_li_col;       // dif       
            cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_shi);           
            O.rgb += 0.4 *(cosa>0.? cosa:0.) * came_li_col ; // spec

            // fresnel
            //O.rgb += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.6 * sky_col;

            // fog
            O.rgb = mix(O.rgb, sky_col, 1.0 - exp(-8.E-3 * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

// const
const float 
    PI = abs(atan(0.,-1.));

/// 1D transformations
float signeveodd(float x){
    return sign(mod(x-0.5,2.)-1.);}

float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5);
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5);
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );
    return (pp+pn);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb) {
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a) {
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z) {
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w) {
    return cmul(z,cinv(w));}

vec2 cpow(vec2 z, int n) {
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));}

vec2 csqr(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y );}

vec2 ccos(vec2 z) {
    vec2 d=vec2(exp(z.y),exp(-z.y));
	return vec2(cos(z.x)*(d.x+d.y)*.5,-sin(z.x)*(d.x-d.y)*.5);}

float binomial(float a, int n) {
   float s = 1.0;
   for (int i = n; i >= 1; i--,a--) {
     s *= float(a)/float(i);
   }
   return s;
}

vec2 expi(float x) {
  return vec2(cos(x),sin(x));
}

float gamma(float z) {
  const float[8] p = float[](
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  );
  z -= 1.0;
  float x = 0.99999999999980993;
  for (int i = 0; i < 8; i++) {
    float pval = p[i];
    x += pval/(z+float(i+1));
  }
  float t = z + 8.0 - 0.5;
  return sqrt(2.0*PI) * pow(t,z+0.5) * exp(-t) * x;
}

// The Beta function
float B(float a, float b) {
  return (gamma(a)*gamma(b))/gamma(a+b);
}

vec2 inversesc(vec2 z, int K) {
  float cn[6];
  for (int n = 1; n <= 5; n++) {
    cn[n] = binomial(float(n)-1.0+2.0/float(K),n)/float(1+n*K);
  }
  float C = B(1.0/float(K),1.0-2.0/float(K))/float(K);
  z *= C;
  vec2 h = cpow(z,int(K));
  float T1 = -cn[1];
  float T2 = -cn[2]+float(K+1)*pow(cn[1],2.0);
  float T3 = -cn[3]+float(3*K+2)*(cn[1]*cn[2]-float(K+1)/2.0*pow(cn[1],3.0));
  float T4 = -cn[4]+float(2*K+1)*(2.0*cn[1]*cn[3]+pow(cn[2],2.0)-float(4*K+3)*
                                  (pow(cn[1],2.0)*cn[2]-float(K+1)/3.0*pow(cn[1],4.0)));
  float T5 = -cn[5]+float(5*K+2)*(cn[1]*cn[4]+cn[2]*cn[3]+float(5*K+3)*
            (-0.5*pow(cn[1],2.0)*cn[3]-0.5*cn[1]*pow(cn[2],2.0)+float(5*K+4)*
            (pow(cn[1],3.0)*cn[2]/6.0-float(K+1)*pow(cn[1],5.0)/24.0)));
  vec2 X = vec2(1,0)+h/pow(C,float(K));
  vec2 w = cmul(z,vec2(1,0) + T1*h + T2*cpow(h,2) + T3*cpow(h,3) + T4*cpow(h,4) + cdiv(T5*cpow(h,5),X));
  return w;
}

// grid's
vec2 recgrid2rec(in vec2 Z, out vec2 Z_id, out float  z_sr){ 
    const float PI_4 = PI/4.;
    const float SQRT2 = sqrt(2.);
    const float SQRT_2 = 1./SQRT2;
    Z = crot(Z,PI_4);
    Z/= SQRT_2;
    Z_id = 2.*floor(Z*.5+0.5);
    vec2 Z_lo = Z-Z_id;
    Z = Z_lo;
    z_sr = signeveodd(0.5*(Z_id.x+Z_id.y));   
    Z_id = vec2(signeveodd(Z_id.x*0.5),signeveodd(Z_id.y*0.5));
    Z.x *= Z_id.x;
    Z.y *= Z_id.y;
    Z *= SQRT_2;
    return crot(Z,-PI_4);
}

// platonics polynomial barycentric approximation
vec2 cHexaPolyArc2disk(vec2 z) {
    // https://people.maths.ox.ac.uk/trefethen/lightning.html
    const int NUM_j = 18; // poles
    const vec2[] rf = vec2[NUM_j]
    (vec2( 0.7072, -0.7070)
    ,vec2(-0.7070,  0.7072)
    ,vec2(-0.0377, -0.9993)
    ,vec2( 0.7072,  0.7070)
    ,vec2(-0.7072, -0.7070)
    ,vec2(-0.0377,  0.9993)
    ,vec2( 0.5452, -0.8383)
    ,vec2( 0.9936,  0.1126)
    ,vec2(-0.9936,  0.1126)
    ,vec2(-0.7983, -0.6022)
    ,vec2(-0.7563,  0.6542)
    ,vec2( 0.6542,  0.7563)
    ,vec2( 0.7563, -0.6542)
    ,vec2(-0.6235,  0.7818)
    ,vec2(-0.7167, -0.6974)
    ,vec2(-0.6542, -0.7563)
    ,vec2( 0.7342,  0.6790)
    ,vec2( 0.7025, -0.7117));

    const vec2[] rw = vec2[NUM_j]
    (vec2( 0.0135,  0.0   )
    ,vec2( 0.0008,  0.0001)
    ,vec2(-0.5762,  0.1282)
    ,vec2( 0.0013,  0.0002)
    ,vec2(-0.0135, -0.0085)
    ,vec2( 0.1239, -0.0601)
    ,vec2(-0.2162, -0.3769)
    ,vec2( 0.1197, -0.1327)
    ,vec2( 0.2544,  0.0708)
    ,vec2( 0.3228,  0.3050)
    ,vec2( 0.0377, -0.0056)
    ,vec2( 0.0362, -0.0135)
    ,vec2( 0.1439, -0.0871)
    ,vec2( 0.0551,  0.0066)
    ,vec2(-0.0935,  0.1492)
    ,vec2(-0.2397,  0.1216)
    ,vec2( 0.0218, -0.0211)
    ,vec2( 0.0077, -0.0763));

    const vec2[] rz = vec2[NUM_j]
    (vec2( 0.366242, -0.365216)
    ,vec2(-0.365216,  0.366242)
    ,vec2(-0.012767, -0.414156)
    ,vec2( 0.366242,  0.365216)
    ,vec2(-0.366242, -0.365216)
    ,vec2(-0.012767,  0.414156)
    ,vec2( 0.216186, -0.397592)
    ,vec2( 0.413695,  0.038296)
    ,vec2(-0.413695,  0.038296)
    ,vec2(-0.391705, -0.251311)
    ,vec2(-0.383838,  0.291533)
    ,vec2( 0.291533,  0.383838)
    ,vec2( 0.383838, -0.291533)
    ,vec2(-0.266502,  0.388876)
    ,vec2(-0.372411, -0.341303)
    ,vec2(-0.291533, -0.383838)
    ,vec2( 0.378349,  0.316470)
    ,vec2( 0.350877, -0.369995));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++) {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zn += cmul(rf[j], zj); // sum nominator
        zd += zj;              // sum denominator
    }
    return cdiv(zn, zd);
}

// map spherical platonic solid pyramidal sectors to one sector located at -z-axis.
vec3 hexa_cells(in vec3 p, out float cell_id, out float sr){
	//// hexagon
    // cells +X, -X, +Y, -Y,+Z, -Z
    // cell walls defined by the face_normals
    bool nBA = dot(vec3( 1., 0.,-1.),p)>=0.;
	bool nCA = dot(vec3( 0., 1.,-1.),p)>=0.;
	bool nDA = dot(vec3( 1., 1., 0.),p)>=0.;
	bool nCB = dot(vec3(-1., 1., 0.),p)>=0.;
	bool nDB = dot(vec3( 0., 1., 1.),p)>=0.;
	bool nDC = dot(vec3( 1., 0., 1.),p)>=0.;
    // map (+X, -X, +Y, -Y,+Z, -Z)-cells to (-Z)-cell by rotation
    // rotation angles
    const float a90 = PI/2.; // a = 90째       
    const vec2 ei_a0   = vec2(1., 0.);             // a = 0째 
    const vec2 ei_a90  = vec2(cos(a90), sin(a90)); // a = 90째

    vec2 ei_y; vec2 ei_x;
	//inside cell +X: +X --> -Z
	if(nBA && nDA && !nCB && nDC) {
		cell_id = 0.;
		ei_y = ei_a90;
		ei_x = ei_a0;
	}
	//inside cell -X: -X --> -Z
	else if(!nBA && !nDA && nCB && !nDC) {
		cell_id = 1.;
		ei_y = -ei_a90;
		ei_x = ei_a0;
	}
    //inside cell +Y: +Y --> -Z
	else if(nCA && nDA && nCB && nDB) {
		cell_id = 2.;
		ei_y = -ei_a90;
		ei_x = -ei_a90;
	}
	//inside cell -Y: -Y --> -Z
	else if(!nCA && !nDA && !nCB && !nDB ) {
		cell_id = 3.;
		ei_y = ei_a90;
		ei_x = ei_a90;
	}
	//inside cell +Z: +Z --> -Z
	else if(!nBA && !nCA && nDB && nDC) {
		cell_id = 4.;
		ei_y = -ei_a0; // 180째
		ei_x = ei_a0;
	}
	//inside cell -Z: -Z --> -Z
	else if(nBA && nCA && !nDB && !nDC) {
		cell_id = 5.;
		ei_y = ei_a0;
		ei_x = ei_a0;
	}        
	// cell orientation
	p.zx = cmul(p.zx,ei_y); // rotate around y-axis
	p.yz = cmul(p.yz,ei_x); // rotate around x-axis
	// non cyclic
	sr = 4.;
    return p;
}

// distribution
float sig(float x) {
    return x < 0. ? -1. : +1.; 
    }

float dist(float x, float e) {
    // e in [0., +inf]
    return sign(x)*abs( pow(abs(x), abs(e)) );
    }

vec2 r_poly_dist(vec2 z, vec2 e, float sh_a) {   
    /// r-polygon-distribution
    float r = length(z); z = z/r;
    float d0 = 0.5 *abs(e.y-e.x);
    float e2 = e.x + d0 + d0 * (2.*smoothstep(-1., 1., -sh_a) - 1.);
    //    float e2 = e.x + d0 * 2. * smoothstep(-1., 1., -sh_a);
    return dist(r, e2) * z;
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// sdf
float opIntersection( float d1, float d2 ) {
    return max(d1,d2); }

float opSmoothUnion( in float d1, in float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere( vec3 p, float s ) {
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, float r ) {
  return length(p.xz)-r;
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// color
vec3 h2rgb(float h){
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing
}

vec3 hsv2rgb(float h, float s, float v){     
    return v * mix( vec3(1.0), h2rgb(h), s);
}

// srgb <--> rgb (linear)
vec3 srgb2rgb(vec3 col) {
    return pow(col, vec3(2.2));
}

vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
