void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy;
    vec2 center = iResolution.xy * 0.5;
    float angle = (iTime * 0.20);
    float radius = 0.45 * iResolution.y;
    float radius2 = 0.05 * iResolution.y;
    vec2 extent = vec2 (-cos(angle), sin(angle));


    vec2 center2 = iResolution.xy * 0.5;

    vec3 red = vec3(1.0,0.0,0.0);
    vec3 yellow=vec3(1.0,1.0,0.0);
    vec2 velocity = vec2(1.0f, 1.0f);
    float time = mod(iTime * 1.0 / 1.0, 2.0);
    fragColor = vec4 (1.0,1.0,1.0,1.0);

    float d = length (center - uv);
    float d2 = length (center2 - uv);
    if (d2 <=radius2)
        fragColor = vec4(red,1.0);
    else if (d <= radius){
        fragColor = vec4 (yellow,1.0);
    }

  vec2 v = vec2(-extent.y,extent.x);
  vec2 _uv;
  _uv.x = dot(uv - center, extent);
  _uv.y = dot(uv - center, v);
  if (_uv.x >= 0.0 && _uv.y >= 0.0 && _uv.x <= radius && _uv.y <= 4.0)
        fragColor = vec4 (0.0,0.0,0.0,1.0);

}
