// 260 chars by @Xor, then 255 via some visual tweaks
void mainImage(out vec4 f, vec2 c)
{
    vec2 r = iResolution.xy,
         u = (c+c - r) / r.y;
    vec3 l = cos(iTime) * vec3(1, .4, tan(iTime)),
         n = vec3(u, sqrt(1. - dot(u, u))),
         d = max(dot(n, l), 0.) * vec3(0, 2, 4);
    f.rgb = 1. - exp(-(d*d + vec3(8, 2, 1) *
            pow(reflect(-l, n).z, 3.)) *
            smoothstep(.9,.5, length(u)));
}

