float f_n_inout(float n){
    if(n > .5){
        return 0.5 - mod(n, 0.5); 
    }
    return n;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trpino = (fragCoord - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_fc_nor = fragCoord.xy / iResolution.xy;
    float n_tau = 6.2831;
    vec2 o_p = vec2(0., 0.);
    vec2 o_diff = o_trpino-o_p;
    float n_dist = length(o_diff);
    float n_ang_nor = fract(((atan(o_diff.y, o_diff.x)+(n_tau/2.))/n_tau)+0.5);
    float n_its = 9.;
    float n_ang_snapped = floor(n_ang_nor / (1./n_its));
    float n_ang_snapped_nor = n_ang_snapped / n_its;
    vec2 o_p_center = vec2(0.);
    float n_dist_center = length(o_trpino-o_p_center);
    float n_a = fract(n_ang_snapped_nor);
    vec2 o_p_onangle_snapped = vec2(
        cos(n_a*n_tau+(1./n_its/2.)*n_tau)*n_dist_center,
        sin(n_a*n_tau+(1./n_its/2.)*n_tau)*n_dist_center
    );
    float n_dist_o_p_onangle_snapped = length(
        o_trpino - o_p_onangle_snapped
    );
    float n = n_dist_o_p_onangle_snapped*n_its;
    //n = smoothstep(0.1, 0.11, n);
    n = n+(sin(n_dist_center*n_tau+iTime)*0.5+0.5);
    fragColor = vec4(n);
}
