void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.y ;

    vec3 red    = vec3(0.929, 0.125, 0.141);
    vec3 green  = vec3(0.153, 0.557, 0.263);
    vec3 yellow = vec3(0.996, 0.741, 0.067);
    vec3 white  = vec3(1.0, 1.0, 1.0);
    vec2 centre = vec2(0.5, 0.5);

    vec3 col = white;

    mat2 scale = mat2(iResolution.x/iResolution.y, 0., 0., 1.);

    float len = distance(uv, vec2(0.));

    if (len < 0.25) {
        col = yellow;
        if(mod(len * iResolution.y + 2. * cos(fragCoord.x/3. + iTime * 5.), 4.) < 1.)
            col *= 0.5;
    }else{
        if (uv.y < -1./6.) {
            col = green;
            if (mod(fragCoord.y + iTime * 10., 3.) < 1.)
                col *= 0.5;
        } else if (uv.y > 1./6.) {
            col = red;
            if (mod(fragCoord.y + 2.*cos(fragCoord.x/3. + iTime * 5.), 3.) < 1.)

                col *= 0.5;
        }
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
