    float val = (0.37785045725124383) * cos(0.3141592653589793 * timeSlow)+(2.3364232140542085) * cos(1.5707963267948966 * timeSlow)+(18.717028467910303) * cos(1.6336281798666925 * timeSlow)+(3.696330823238726) * cos(1.7592918860102842 * timeSlow)+(9.578403455815018) * cos(1.8221237390820801 * timeSlow)+(1.3625736929657797) * cos(1.8849555921538756 * timeSlow)+(2.7942202996192163) * cos(1.9477874452256716 * timeSlow)+(1.7711610615255986) * cos(2.0106192982974678 * timeSlow)+(0.9713881229829386) * cos(2.0734511513692637 * timeSlow)+(1.721581029181113) * cos(2.1362830044410597 * timeSlow)+(1.0957706783455479) * cos(2.199114857512855 * timeSlow)+(1.0743272383031455) * cos(2.261946710584651 * timeSlow)+(1.9237696070026973) * cos(2.324778563656447 * timeSlow)+(3.0501469540827926) * cos(2.4504422698000385 * timeSlow)+(3.57347625097854) * cos(2.57610597594363 * timeSlow)+(4.164819657445082) * cos(2.701769682087222 * timeSlow)+(0.9780693241331418) * cos(2.764601535159018 * timeSlow)+(13.732993114269574) * cos(2.827433388230814 * timeSlow)+(8.990633102958629) * cos(2.9530970943744057 * timeSlow)+(6.260044998909341) * cos(3.141592653589793 * timeSlow)+(2.422189622275397) * cos(3.267256359733385 * timeSlow)+(15.933357175405717) * cos(3.4557519189487724 * timeSlow)+(0.3716019058694118) * cos(3.581415625092364 * timeSlow)+(4.2045240500285095) * cos(3.6442474781641603 * timeSlow)+(0.43314671398601945) * sin(0.3141592653589793 * timeSlow)+(10.259948928086486) * sin(1.5707963267948966 * timeSlow)+(12.083461743869616) * sin(1.6336281798666925 * timeSlow)+(8.091002375361459) * sin(1.7592918860102842 * timeSlow)+(-4.947019252504502) * sin(1.8221237390820801 * timeSlow)+(0.0600410407611961) * sin(1.8849555921538756 * timeSlow)+(5.842610601241493) * sin(1.9477874452256716 * timeSlow)+(1.3074994797075126) * sin(2.0106192982974678 * timeSlow)+(2.0786788597400236) * sin(2.0734511513692637 * timeSlow)+(1.876456336333239) * sin(2.1362830044410597 * timeSlow)+(2.2983684279760968) * sin(2.199114857512855 * timeSlow)+(1.5756881298204577) * sin(2.261946710584651 * timeSlow)+(0.7598722612130775) * sin(2.324778563656447 * timeSlow)+(3.092277842258946) * sin(2.4504422698000385 * timeSlow)+(1.1479047878773916) * sin(2.57610597594363 * timeSlow)+(2.2552228380551265) * sin(2.701769682087222 * timeSlow)+(-1.1038233102198718) * sin(2.764601535159018 * timeSlow)+(4.851386456130351) * sin(2.827433388230814 * timeSlow)+(13.47166890824398) * sin(2.9530970943744057 * timeSlow)+(-29.19821342041527) * sin(3.141592653589793 * timeSlow)+(-1.5531119423375312) * sin(3.267256359733385 * timeSlow)+(-2.646483440926437) * sin(3.4557519189487724 * timeSlow)+(6.665382994344453) * sin(3.581415625092364 * timeSlow)+(-4.85667950496648) * sin(3.6442474781641603 * timeSlow);
    return -val/100.0;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float heart(vec2 p) {
  float beatFraction = computeBeat();
  p.x = pabs(p.x, 0.05) * (0.90 + 0.10 * beatFraction);
  p.y = p.y * (0.93 + 0.07 * beatFraction);
  if( p.y+p.x>1.0 )
      return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
  return sqrt(min(dot2(p-vec2(0.00,1.00)),
                  dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float df(vec2 p) {
  vec2 hp = p;
  const float hz = 1.0;
  hp /= hz;
  hp.y -= -0.6;
  float d = heart(hp)*hz;
  return d;
}

float hf(vec2 p) {
  float d = df(p);
  float h = (-20.0*d);
  h = tanh_approx(h);
  h -= 3.0*length(p);
  h = pmin(h, 0.0, 1.);
  h *= 0.25;
  return h;
}

vec3 nf(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = hf(p + e.xy) - hf(p - e.xy);
  n.y = hf(p + e.yx) - hf(p - e.yx);
  n.z = 2.0*e.x;

  return normalize(n);
}

vec2 hash(vec2 p) {
  p = vec2(dot (p, vec2 (127.1, 311.7)), dot (p, vec2 (269.5, 183.3)));
  return -1. + 2.*fract (sin (p)*43758.5453123);
}

float noise(vec2 p) {
  const float K1 = .366025404;
  const float K2 = .211324865;

  vec2 i = floor (p + (p.x + p.y)*K1);

  vec2 a = p - i + (i.x + i.y)*K2;
  vec2 o = step (a.yx, a.xy);    
  vec2 b = a - o + K2;
  vec2 c = a - 1. + 2.*K2;

  vec3 h = max (.5 - vec3 (dot (a, a), dot (b, b), dot (c, c) ), .0);

  vec3 n = h*h*h*h*vec3 (dot (a, hash (i + .0)),dot (b, hash (i + o)), dot (c, hash (i + 1.)));

  return dot (n, vec3 (70.));
}

float fbm(vec2 pos, float tm) {
  vec2 offset = vec2(cos(tm), sin(tm*sqrt(0.5)));
  float aggr = 0.0;

  aggr += noise(pos);
  aggr += noise(pos + offset) * 0.5;
  aggr += noise(pos + offset.yx) * 0.25;
  aggr += noise(pos - offset) * 0.125;
  aggr += noise(pos - offset.yx) * 0.0625;

  aggr /= 1.0 + 0.5 + 0.25 + 0.125 + 0.0625;

  float f = (aggr * 0.5) + 0.5;

  return f;
}

float divf(float offset, float f) {
  const float goff = 0.2;
  const float gfloor = 0.001;
  float r = abs(goff + offset - f);
  r = max(r, gfloor);
  return r;
}

// This way of computing "lightning" I found at shadertoy. Unfortunately I don't remember where.
vec3 lightning(vec2 pos, vec2 pp, float offset) {
  vec3 sub = 0.03*vec3(0.0, 1.0, 2.0).zyx*length(pp);

  float time = TIME+123.4;
  float stime = time/200.0;
  vec3 col = vec3(0.0);
  vec2 f = 10.0*cos(vec2(sqrt(0.5), 1.0)*stime)+vec2(0.0, -11.0)*stime;
  const float glow = 0.0125;
  const float goff = 0.2;
  const float gfloor = 0.001;
  for (float i = 0.0; i < 3.0; ++i) {
    vec3 gcol0 = (1.0+cos(0.50*vec3(0.0, 1.0, 2.0) +time+3.0*pos.x-0.33*i));
    vec3 gcol1 = (1.0+cos(1.25*vec3(0.0, 1.0, 2.0) +2.*time+pos.y+0.25*i));
    float btime = stime*85.0 + (i);
    float rtime = stime*75.0 + (i);
    float div1 = divf(offset, fbm((pos + f) * 3.0, rtime));
    float div2 = divf(offset, fbm((pos + f) * 2.0, btime));
    float d1 = offset * glow / div1;
    float d2 = offset * glow / div2;
    col += (d1 * gcol0)-sub;
    col += (d2 * gcol1)-sub;
  }

  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  float aa = 4.0/RESOLUTION.y;
  float d = df(p);
  float h = hf(p);
  vec3 n = nf(p);
  const vec3 lp = vec3(-4.0, -5.0, 3.0);
  const vec3 ro = vec3(10.0, 00.0, 0.0);
  vec3 p3 = vec3(p, h); 
  vec3 rd = normalize(p3-ro);
  vec3 ld = normalize(lp-p3);
  vec3 r = reflect(rd, n);
  float diff = max(dot(ld, n), 0.0);
  vec3 dcol = dbcol*mix(vec3(0.15), vec3(1.0), diff);
  float spe = pow(max(dot(ld, r), 0.0), 3.0);
  vec3 scol = spe*sbcol;
  float gd = d+0.0;
  vec2 gp = p;
  vec3 gcol = lightning(gp, pp, gd);
  vec3 hcol = dcol;
  hcol += scol;
  vec3 col = vec3(1.0);
  col += gbcol/max(0.01*(dot2(p)-0.15), 0.0001);
  col += gcol;
  col = mix(col, hcol, smoothstep(0.0, -aa, d));
  col = mix(col, fbcol, smoothstep(0.0, -aa, abs(d+0.01)-0.01));
  col *= smoothstep(1.75, 0.5, length(pp));

  col = aces_approx(col); 
  col = sqrt(col); 
  return col;
}

float laplacian[9] = float[9](0.0, -1.0, 0.0, 
                            -1.0, 4.0, -1.0,
                             0.0, -1.0, 0.0);
const float sharpness = 0.0;

vec4 sharpness_filter(float[36] color_table) {
    float color[4] = float[4](0.0,0.0,0.0,0.0);
    for (int j = 0; j < 4; ++j) {
        for (int i = 0; i < 9; ++i) {
            color[j] += sharpness * laplacian[i] * color_table[i*4];
        }
     }

    return vec4(color[0] + color_table[18], color[1] + color_table[19], color[2] + color_table[20], 1.0);
}

vec4 getColorAtq(vec2 q) {
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  return vec4(col.yxz, 1.0);
}

float[36] get_color_table(vec2 fragCoord) {
    float A[36];
    vec2 q;
    vec4 c;
    for (int i = -1; i<2;++i) {
        for (int j = -1; j < 2; ++j) {
            q = (fragCoord + vec2(i,j) )/RESOLUTION.xy;
            c = getColorAtq(q);
            A[18 + i*3*4 +j*4] = c.x; 
            A[18 + i*3*4 +j*4 + 1] = c.y; 
            A[18 + i*3*4 +j*4 + 2] = c.z; 
            A[18 + i*3*4 +j*4 + 3] = c.w; 
        }
    }
    return A;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  float A[36] = get_color_table(fragCoord);
  fragColor = sharpness_filter(A);
}
