/*=======Constants=======*/

//Lighting constants
const float ambient = 0.1; //Ambient light.
const float sundot = 0.999; //Controls sun size. Smaller = Larger sun.

const vec3 suncolor = vec3(1.0, 1.0, 1.0);

//Atmospheric constants
const float scatterOutStrength = 200.0; //Out-scattering strength
const float scatterInStrength = 60.0; //In-scattering strength
const float mieScatterStrength = 100.0; //Mie scattering strength

const float cloudDensity = 1000.0;

const float cloudLowAlt = 0.005;
const float cloudHighAlt = 0.025;

const float cloudAvgAlt = (cloudLowAlt + cloudHighAlt)/2.0;
const float cloudReciprocalDiff = 1.0/(cloudHighAlt-cloudLowAlt);

const vec3 atmcol = vec3(0.11, 0.41, 1.0); //Blue color of atmo (DO NOT CHANGE)
const vec3 rcol = exp(-0.15*atmcol);

const float atmoradius = 1.1;

//Render constants
const vec3 center = vec3(-0.0, 0.0, 2.5);

/*
 * ================================
 *            Sun Sizes
 * ================================
 *
 * 0.866 gives a 60 degree wide sun
 * 0.906 gives a 50 degree wide sun
 * 0.940 gives a 40 degree wide sun
 * 0.966 gives a 30 degree wide sun
 * 0.985 gives a 20 degree wide sun
 * 0.991 gives a 15 degree wide sun
 * 0.996 gives a 10 degree wide sun
 * 0.999 gives a  5 degree wide sun
 * 0.9994 gives a 4 degree wide sun
 * 0.9997 gives a 3 degree wide sun
 * 0.9998 gives a 2 degree wide sun
 * 0.99996 gives a 1 degree wide sun
 * 0.99999 gives a realistic 0.5 degree wide sun
