float press(vec3 p) {
    float alt = sqrt(dot(p,p))-1.0;
    return exp(-50.0*alt);
}

float magnitude(vec3 p) {
    return sqrt(dot(p,p));
}

float opticalDepth(vec3 p, vec3 dir, float radius) {
    float ltca = dot(dir, -p);
    float ld = sqrt(dot(p,p)-ltca*ltca);
    float lthc = sqrt(radius*radius-ld*ld);

    float ldepth = ltca + lthc;

    float samples = 5.0;
    float ret = 0.0;

    for (float i = 0.0; i < 1.001; i += 1.0/samples) {
        vec3 pos = p + dir*ldepth*i;
        ret += press(pos)*ldepth/samples;
    }
    return ret;
}

float rayleighPhase(float c) {
    return 1.0 + c*c;
}

float miePhase(float c) {
    float g = 0.75;
    return pow(1.0-g,3.0)*(1.0+c*c)/(2.0*(1.0+g*g-2.0*g*c));
}

float cloudPhase(float c) {
    return 0.2+0.8*sqrt(1.0-c*c);
}

float cloudFunc(vec3 pos) {
    vec3 np = pos-center;
    np += 0.01*simplex3d(100.0*pos+iTime);
    np = normalize(np);
    float alt = magnitude(pos-center)-1.0;
    float altMult = exp(-10.0*cloudReciprocalDiff*cloudReciprocalDiff*pow(alt-cloudAvgAlt,2.0));

    float density = texture(iChannel3, vec2(0.5+atan(np.x,np.z)/(2.0*pi),0.5+atan(np.y)/pi)).x;

    return altMult*(min(density,0.8)-0.5);
}

float apos(float a, float b) {
    if (a == b) {
        return a;
    }

    float dnm = a/(a-b);
    float d = min(1.0, max(dnm, 0.0));
    if (a > 0.0 && b > 0.0) {
        return b * (1.0-dnm) * (1.0 - pow(a/b, 2.0))/2.0;
    }
    return (max(b, 0.0) * (1.0 - d) + max(a, 0.0) * d)/2.0;
}

float simpson(float s, float m, float e) {
    return s + 4.0*m + e;
}

