void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Constants

    float radius = 1.0;

    //vec3 lightdir = normalize(vec3(sin(iTime/5.0),0.0,-cos(iTime/5.0)));
    vec3 lightdir = normalize(vec3(0.01,0.0,-0.01));

    // Normalized pixel coordinates (from 0 to 1)
    float m = min(iResolution.x, iResolution.y);

    vec2 uv = fragCoord/m;
    vec2 xy = 2.0*uv-vec2(iResolution.x/m, iResolution.y/m);

    // Direction
    float vFOV = 60.0;
    vFOV /= 2.0;

    vec3 D = normalize(vec3(tan(vFOV*3.14159/180.0)*xy,1.0));

    //Sphere
    float tca = dot(center, D);
    float d = sqrt(dot(center, center)-tca*tca);
    float thc = sqrt(radius*radius-d*d);
    vec3 norm = normalize((D*(tca-thc))-center);

    float cloudRegionStartD = 0.0;
    float cloudRegionEndD = 0.0;

    float lowRegionStartD = 0.0;
    float lowRegionEndD = 0.0;

    float hcthc = 0.0;

    if (d < 1.0 + cloudHighAlt) {
        hcthc = sqrt(pow(radius+cloudHighAlt,2.0)-d*d);
        cloudRegionStartD = max(tca - hcthc, 0.0);
        cloudRegionEndD = tca + hcthc;

        if (d < 1.0 + cloudLowAlt) {
            hcthc = sqrt(pow(radius+cloudLowAlt,2.0)-d*d);
            lowRegionStartD = max(tca - hcthc, 0.0);
            lowRegionEndD = tca + hcthc;
        }
    }

    vec3 coord = norm;
    float thccopy = thc;

    coord *= mat3(
        cos(iTime/5.0), 0.0, sin(iTime/5.0),
        0.0, 1.0, 0.0,
        sin(-iTime/5.0), 0.0, cos(iTime/5.0)
    );

    //Background
    vec3 col = vec3(0.0);
    float dotp = dot(D, lightdir);
    if (dotp > sundot) {
        col = vec3(pow(2.0*(dotp-sundot)/(1.0-sundot),6.0));
    }

    //Lighting
    if (d < radius && tca >= 0.0) {
        float specnoise = 1.0+0.07*simplex3d(40.0*coord+iTime);
        norm *= specnoise*0.05+0.95;

        float spherenoise = simplexfract(coord);
        vec3 spherecolor = vec3(0.0,0.05/max(abs(spherenoise)*2.0,0.1),specnoise);
        vec3 specular = vec3(pow(max(dot(norm, normalize(lightdir - D)),0.0), 100.0));
        if (spherenoise > 0.0) {
            spherecolor = vec3(0.0, 0.7*(0.5*simplex3d(coord*3.0)+1.0-0.6*abs(coord.y/radius)), 0.4);
            if (spherenoise < 0.05) {
                spherecolor = 20.0*((spherenoise)*spherecolor+(0.05-spherenoise)*vec3(1.0,1.0,0.5));
            }
            if (spherenoise > 0.5) {
                spherecolor = vec3(0.5);
            }
            if (spherenoise > 0.6+0.07*simplex3d(coord*30.0+0.4)) {
                spherecolor = vec3(1.0);
            }
            if (dot(lightdir, norm) > -sqrt(radius*radius-spherenoise*spherenoise)) {
                vec3 dirY = vec3(0.0, 0.006, 0.0);
                vec3 dirZ = 0.006*norm;
                vec3 dirX = 0.006*normalize(cross(dirY, dirZ));
                dirY = 0.006*normalize(cross(dirZ,dirX));

                float normX = simplexfract(coord+dirX)-simplexfract(coord-dirX);
                float normY = simplexfract(coord+dirY)-simplexfract(coord-dirY);
                float normZ = simplexfract(coord+dirZ)-simplexfract(coord-dirZ);
                norm = 0.7*norm+0.2*normalize(dirX*normX + dirY*normY + dirZ*normZ);
                norm = normalize(norm);
            }

            specular = vec3(0.0);
        }
        vec3 diffuse = vec3(max(dot(lightdir, norm),ambient))*spherecolor;

        col = diffuse*1.0+0.5*specular;
    }

    if (d <= atmoradius) {
        //Calc atmo
        thc = sqrt(atmoradius*atmoradius-d*d);
        vec3 sp = D*(tca-thc);
        if (thc > tca) {
            sp = vec3(0.0);
        }
        float depth = magnitude(D*(tca+thc) - sp);
        if (d < radius && tca > 0.0) {
            depth = magnitude(D*(tca-thccopy)-sp);
        }

        //float samples = 5.0; //Low quality
        float samples = 10.0; //Medium atmospheric quality
        //float samples = 20.0; //High quality

        float cloudSamples = 15.0;
        float subSamples = 1.0;

        float totalCloudDepth = cloudRegionEndD - cloudRegionStartD;

        float dtrue = d;

        if (tca < 0.0) {
            dtrue = sqrt(dot(center, center));
        }

        if (dtrue < 1.0 + cloudLowAlt) {
            totalCloudDepth -= lowRegionEndD - lowRegionStartD;
            totalCloudDepth /= 2.0;
        }

        float cloudRegionAddm = totalCloudDepth*samples/(cloudSamples*depth);

        float addm = 1.0;

        for (float i = 0.0001; i < 1.0; i += 0.0) {

            vec3 pos = sp + D*depth*i*0.9998;
            float dense = press(pos-center);

            float ltca = dot(lightdir, center-pos);
            float ld = sqrt(dot(center-pos,center-pos)-ltca*ltca);
            float lthc = sqrt(atmoradius*atmoradius-ld*ld);

            float pmss = 0.0;
            float alt = magnitude(pos-center)-1.0;

            //Cloud generation

            float cloudf = cloudFunc(pos);            
            //col = 0.9*col+0.1*vec3(protoMult/ceil(protoMult));
            if (alt > cloudHighAlt) {
                addm = 1.0;
            }

            if (alt <= cloudLowAlt && tca < 0.0) {
                addm = (lowRegionEndD - lowRegionStartD)*samples/(subSamples*depth);
            }

            float cctca = 0.0;
            hcthc = 0.0;
            if (alt <= cloudHighAlt && alt > cloudLowAlt) {
                cctca = dot(center-pos, lightdir);
                float ccd = sqrt(dot(center-pos, center-pos)-cctca*cctca);
                hcthc = sqrt(pow(radius+cloudHighAlt,2.0)-ccd*ccd);

                addm = cloudRegionAddm;
                float cf2 = cloudFunc(pos + D*depth*addm/samples);
                float simp = (max(0.0,cloudf)+max(0.0,cf2));
                pmss += mieScatterStrength*(cctca+hcthc)*cloudDensity*1.0*pow(simp,2.0);
            }

            if (ld >= 0.99 || ltca <= 0.0) {
                if (ltca < 0.0) {
                    ld = sqrt(dot(center-pos,center-pos));
                }
                float lm = min(1.0, 100.0*(ld-0.99));

                float sinFactor = 2.0*sqrt(1.0-pow(dot(D,lightdir),2.0));
                sinFactor += sqrt(0.5-0.5*dot(D,lightdir));
                sinFactor /= 3.0;

                //if (D.y < 0.0) {
                //    sinFactor = log(9.0-8.0*dot(D,lightdir))/log(17.0);
                //}

                float od = opticalDepth(pos-center, lightdir, atmoradius);
                vec3 outColor = pow(rcol, vec3(scatterOutStrength*od));

                col += scatterInStrength*min(lm,1.0)*sinFactor*outColor*rayleighPhase(dot(D,lightdir))*dense*atmcol*depth*addm/samples;
                col += mieScatterStrength*outColor*min(lm,1.0)*dense*miePhase(dot(D,lightdir))*depth*addm/samples;

                col *= exp(-5000.0*(cctca+hcthc)*pmss*od*depth*addm/samples);
                col += 7.0*pmss*outColor*min(lm,1.0)*cloudPhase(dot(D,lightdir))*depth*addm/(samples*max((cctca+hcthc),1.0));


                //col += cctca+hcthc;

                //col *= exp(-50.0*pmss*od*depth*addm/samples);
                //if (pmss > 0.0) {
                //    col += vec3(0.1);
                //}
            }
            col *= pow(rcol, vec3(depth*dense*scatterOutStrength*addm/samples));
            i += addm/samples;
        }

        //col = vec3(pmss);
    }

    // Output to screen

    //if (d < 1.0+cloudHighAlt) {
    //    col = vec3(cloudRegionEndD);
    //}

    //col = 10.0*(texture(iChannel3, fragCoord/iResolution.xy).xyz-0.5);
    //col = texture(iChannel3, fragCoord/iResolution.xy).xyz;

    //col.x = 0.0;
    //col.y = 0.0;
    //col.z = 0.0;

    //col = norm;

    fragColor = vec4(pow(1.0-exp(-1.2*pow(col*suncolor,vec3(2.1))), vec3(0.5)),1.0); //Color filter for smoother colors
    //fragColor = vec4(col, 1.0);
}

//Noise maker

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //float m = min(iResolution.x, iResolution.y);

    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= 2.0*pi;
    uv.y -= 0.5;
    uv.y *= pi;

    vec3 coord = vec3(cos(uv.y)*sin(uv.x),sin(uv.y),cos(uv.y)*cos(uv.x));

    //vec3 diff = windChangeSpeed*iTime*timeRate;
    vec3 diff = vec3(cos(windChangeSpeed*iTime*timeRate), windChangeSpeed*iTime*timeRate*0.2, sin(windChangeSpeed*iTime*timeRate));

    fragColor = vec4(vec3(simplex3d(4.0*coord+diff)+0.0*simplex3d(16.0*coord+diff)),1.0);
}

//Curl noise maker
float noisev(vec2 uv) {
    return texture(iChannel0, uv).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 wx = uv;

    wx.y -= 0.5;
    wx.y *= pi;

    float gradStep = 0.01;

    vec2 uvr = uv+vec2(gradStep,0.0);
    vec2 uvl = uv-vec2(gradStep,0.0);
    vec2 uvt = uv+vec2(0.0,gradStep);
    vec2 uvb = uv-vec2(0.0,gradStep);

    uvr.x = mod(uvr.x, 1.0);
    uvl.x = mod(uvl.x, 1.0);
    uvt.x = mod(uvt.x, 1.0);
    uvb.x = mod(uvb.x, 1.0);

    vec2 grad = vec2(noisev(uvr)-noisev(uvl),noisev(uvt)-noisev(uvb));

    fragColor = vec4(-grad.y/cos(wx.y),grad.x,noisev(uv),1.0);
}

//Weather maker

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 wx = fragCoord/iResolution.xy;

    vec2 uv = wx;

    uv.x *= 2.0*pi;
    uv.y -= 0.5;
    uv.y *= pi;

    vec3 coord = vec3(cos(uv.y)*sin(uv.x),sin(uv.y),cos(uv.y)*cos(uv.x));

    float temp = sqrt(1.0-pow(2.0*uv.y/pi,2.0));

    vec4 cv = texture(iChannel0,wx);

    cv.y += 0.0005*(temp-cv.y);
    float maxW = exp((cv.y-1.0)*3.0);

    vec2 adjustCoord = wx-cloudMoveSpeed*timeRate*texture(iChannel1,wx).xy;
    adjustCoord.x = mod(adjustCoord.x, 0.999);
    vec4 pt = texture(iChannel0, adjustCoord);

    //float pMult = 0.4*exp(-pow(1.0/3.0*sin(3.0*uv.y),2.0))+1.6;
    float pMult = 2.0;

    if (iTime < 0.1) { //Page loading heartstart
        pt = vec4(2.0*vec3(exp(-pow(1.0/3.0*sin(3.0*uv.y),2.0))),1.0);
    }

    cv = (1.0-cloudMoveMult)*cv + pt*cloudMoveMult;

    float dcz = 0.01*(maxW-cv.z);

    cv.z += max(dcz,0.0);

    dcz = 0.0015*(maxW/pMult-cv.z);

    cv.z += dcz;
    cv.x += -min(dcz,0.0);
    cv.x *= 0.999;
    fragColor = cv;
}

