#define PI 3.1415926535897932384626433832795

#define ringRadius 0.7
#define ringBlur 0.025
#define ringSize 0.05
#define zoom 0.5
#define ringFadeSize 0.025
#define ringFadeStart ringSize - 0.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;

    float angle = PI/4.;
    uv /= zoom;
    uv += iTime*vec2(0.05, 0.07);
    uv *= mat2(
        cos(angle), sin(angle),
        -sin(angle), cos(angle)
    );

    uv += texture(iChannel0, uv).x*0.05;

    vec2 cellPos = floor(uv);
    vec2 odd = mod(cellPos, 2.);
    float checkerboard = mod(cellPos.x + cellPos.y, 2.);

    vec2 cellCoord = fract(mix(uv.xy, uv.yx, checkerboard)*(1. - odd.yx*2.));


    float ring1dist = abs(max(cellCoord.x, cellCoord.y) - ringRadius);
    float ring2dist = abs(1. - min(cellCoord.x, cellCoord.y) - ringRadius);


    float horizontalFade1 = smoothstep(0., ringFadeSize, abs(cellCoord.y - 1. + ringRadius) - ringFadeStart);
    float horizontalFade2 = smoothstep(0., ringFadeSize, abs(cellCoord.y - ringRadius) - ringFadeStart);

    float verticalFade1 = smoothstep(0., ringFadeSize, abs(cellCoord.x + 1. - ringRadius) - ringFadeStart);
    float verticalFade2 = smoothstep(0., ringFadeSize, abs(cellCoord.x - 2. + ringRadius) - ringFadeStart);

    float ring1 = smoothstep(ringBlur, 0., ring1dist - ringSize)*horizontalFade1*verticalFade1;
    float ring2 = smoothstep(ringBlur, 0., ring2dist - ringSize)*horizontalFade2*verticalFade2;


    vec3 col = (ring1 + ring2)*(0.5 + texture(iChannel1, uv).xyz*0.5);


    fragColor = vec4(col,1.0);
}
