void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 d1 = vec2(length(uv.y),length(uv.x));
    float d = length(uv);
    float c = d;
    vec3 colo = 0.5 + 0.4*cos(uv.xyx+iTime*0.05*vec3(1,2,3)+vec3(1,2,3));
    vec3 colo2 = vec3(0.3 + 0.5*uv.x+sin(iTime*0.25+uv.x),0.35*uv.y+asin(iTime*0.15),0.35+0.5*uv.y*uv.x+cos(iTime*0.22));
    vec3 colo3 = vec3(0.23 + 0.5*uv.y+cos(iTime*0.20),0.35*uv.x+sin(iTime*0.1+uv.y),0.35+0.5*uv.y*uv.x+sin(iTime*0.20));
    vec3 f = colo;
    int p=1;
    int i=1;
    vec2 n= vec2(sin(iTime*0.022), cos(iTime*0.022));
    if(d > .5){ 
        uv.y -= 0.0; // change to move the background. at -0.5 you get a lava lamp like effect
        uv *= 1.7; //scale of the background
        uv *= uv;
        //uv *= abs(uv)+ 0.1;
        //uv -= n*max(0.,d)*2.;

        for(p=1; p < 6; p++){
            for(i=1; i < 6; i++){
                uv.x += sin(float (i)*0.025)*0.02 + sin(iTime*0.013 + float (i+100 + p*53)) * 1.  ;
                uv.y += cos(float (p)*0.5)*0.02 + cos(iTime*0.02+ float (p*100 + i*27)) * 1.  ;
                d = length(uv);
                if(d < .29 + sin(iTime*0.02+float(i+p))*0.05) //change the .2 to change the size of the background orbs
                    f = vec3(sin(float(400*p*i) )*0.3+0.35,0.,sin(float(3000*p*i) )*0.35+0.3)+ vec3(uv.x*0.5);
        }}}
    uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv -= 0.6;
    uv *= 2.5;
    uv.x -= 0.5;

    if(d < .5) { //change the .5 to change the size of the central big orb
        f = (colo + colo2 + colo3)*0.3 + vec3(0.1+uv.x*0.5);
        for(i=1; i < 10; i++){
            uv -= vec2(cos(iTime*0.21 + float(i*1000))*0.5, sin(iTime*0.31+float(i*10000))*0.8);
            d = length(uv);
            if(d < (.2 + sin(iTime*0.25 + float(i*10000))*0.05)){ //change the .2 to change the size of the orbs in the orb
                 f = vec3(sin(float(i)*1000.)*0.2 + uv.x*0.5)+ vec3(sin(iTime*0.13+float(i*2845)),0.2,0.2);}

            }
    } 
    fragColor = vec4(f ,1.0);
}
