//BITONIC SORT

vec3 sbf(vec3 c, vec3 w, float s){
    vec3 k = sin(PI*c*w) * cos(PI*c.yzx*w.yzx) * cos(PI*c.zxy*w.zxy);
    k = mix(k, k* cross(normalize(w), normalize(vec3(2,4,1))), s);
    return k;
}


void mainImage( out vec4 c, in vec2 p )
{
    initialize(p, IFRAME, iResolution);

    int sort_iter = 3*iFrame + 0;

    int id0 = pixelID(p);
    int id1 = getPartner(id0, sort_iter%sort_iters - 1);

    if(id1 < int(pixelnum) && id1 >= 0)
    {
        vec4 A = texelFetch(iChannel0, pixel(id0), 0);
        vec4 B = texelFetch(iChannel0, pixel(id1), 0);

        int zA = pixelID(A.xy);
        int zB = pixelID(B.xy);

        if(id0 > id1) c = (zA>zB)?A:B;
        else c = (zA<zB)?A:B;
    }
    else c = texelFetch(iChannel0, ivec2(p), 0);


    if(sort_iter%sort_iters == 0) //intialize with some random points, in this case pseudo caustic
    {
        vec2 ang = vec2(TWO_PI, PI)*iMouse.xy/R + vec2(0.4);
        mat3 cam = get_cam(ang);

        vec3 campos = -cam*vec3(0.75,0,0);

        vec4 bp = vec4(10001);
