        vec3 p = 0.6*(rand3() - 0.5);
        bp.w = 600.0 + 300.0*p.z;

        float dt = 0.5 + 0.5*sin(SPEED*iTime);
         float dc1 = 0.5 + 0.5*sin(4.0*iTime);
          float dc2 = 0.5 + 0.5*sin(6.0*iTime);
          vec3 pd = vec3(-3,3 ,3);
        for(int i = 0; i < 100; i++)
        {
            pd += dt*600.*sbf(pd, vec3(1,2,3),.9 )/1300.;
            p += dt*30.*sbf(p, pd,.9 )/1300.;
            p += dt*10.*sbf(p.zyx,vec3(1,23,1) + pd*0.3, .9 )/1445.;
