#define MIN_DIST 0.001
#define MAX_DIST 100.
#define PI 3.1415926535

#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdRoundBox( vec3 p, vec3 b, float r )
	{ vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r; }


// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec2 GetDist(vec3 p  )
{
	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf
    float planeDist2 = 50.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+40.0;  //pared frente
    float planeDist6 = p.z+40.0;  //pared atras

    res =opU3(res, vec3(planeDist1,67.0,7.0)); //inf
    res =opU3(res, vec3(planeDist2,67.0,-1.0)); 
    res =opU3(res, vec3(planeDist3,66.0,-1.0)); 
    res =opU3(res, vec3(planeDist4,66.0,-1.0)); 
    res =opU3(res, vec3(planeDist5,65.0,-1.0)); 
    res =opU3(res, vec3(planeDist6,65.0,-1.0));
          p.y=p.y-5.0;

    //rotar centro origen
     float posx=2.5; //es el centro del objeto
     pp=p;
     pp.x=pp.x-posx;
     pp.xz = rotatev2(pp.xz, iTime);
     pp.x=pp.x+posx;

    float sdrb3=  sdRoundBox( pp-vec3(posx,-3.5,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 in
    float sdrb3A= sdRoundBox(pp-vec3(posx,0.0,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 inf
    float sdrb3B= sdRoundBox(pp-vec3(posx,3.5,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 inf

    res =opU3(res, vec3(sdrb3,13.0,-1)); 
    res =opU3(res, vec3(sdrb3A,14.0,-1)); 
    res =opU3(res, vec3(sdrb3B,15.0,-1)); 

   float sdsp1= sdSphere( p-vec3(-10.0,0.0,5.0), 6.0 );
    float sdsp2= sdSphere( p-vec3(14.0,3.0,10.0), 6.0 );
    res =opU3(res, vec3(sdsp1,201.0,-1)); 
    res =opU3(res, vec3(sdsp2,201.0,-1)); 
    return vec2(res.x, res.y);

}

// basic trace, with some LOD
vec2 RayMarch(vec3 ro, vec3 rd, float m) {
    float t;
	vec2 d = vec2(9999.0,-1.0);

	for(int i = 0; i < 200; i++) {
		d = GetDist(ro + rd*t);
		if(d.x < (0.001 + 0.0001*t) || t >= m) break;
        t += d.x*0.67;
	}
	return vec2(t,d.y);
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(0.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float getSoftShadow(vec3 p, vec3 lightPos)
{   float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < 15; i++) {
        float hit = GetDist(p + lightPos * dist).x;
        res = min(res, hit / (dist * lightSize));
        dist += hit;
        if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float ambOcclusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {
        float hr = 0.01 + float(i) * 0.5 / 4.0;
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );
}

//euclidean cameras.
mat3 camera(vec3 o, vec3 l) 
{	vec3 w = normalize(l - o);
	vec3 u = normalize(cross(vec3(0, 1, 0), w));
	vec3 v = normalize(cross(w, u));	
	return mat3(u, v, w);
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
	return sky;
}

vec3 lightingv3(vec3 normal,vec3 p,vec3 lp,vec3 rd, vec3 ro, vec3 color )
{   vec3 lightPos=lp;
    vec3 hit=p;
    vec3 norm=normal;

    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;

    vec3 R = reflect(-light, norm);
    vec3 V = -rd;

    // shadows
    float shadow = getSoftShadow(hit, normalize(lightPos));
    // occ
    float occ = ambOcclusion(hit, norm);

    vec3 ambient= 0.5*color*clamp(0.5 + 0.5*norm.y, 0.0, 1.0);
    vec3 fresnel =  color *  pow(clamp(1.0 + dot(rd, norm), 0.0, 1.0), 2.0);;

    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    vec3 diffuse =  color * dif;

    float specular = pow(max(dot(R, V), 0.), 8.);
     // back
    vec3 back = 0.5 * color * clamp(dot(norm, -light), 0.0, 1.0);
       vec3 colOut = occ*color*(ambient+diffuse*shadow+.35 +back) + vec3(.7,.9,1)*specular*specular;
   return colOut;
}


float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}


float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}  


float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }


float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdRhombus( in vec2 p, in vec2 b ) 
{
    p = abs(p);
    float h = clamp( ndot(b-2.0*p,b)/dot(b,b), -1.0, 1.0 );
    float d = length( p-0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( p.x*b.y + p.y*b.x - b.x*b.y );
}

#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(6.0*antialiasing(0.5),b,d)

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}
vec3 DrawFigSolo(vec3 pColObj, vec3 colOut, float distObj ) 
{  colOut = mix(colOut,pColObj ,S( distObj,0.0)); return colOut; }



vec3 figura001(vec2 p)
{
    p.x= opRep1D(p.x, 4.0 );
    p.y= opRep1D(p.y, 3.0 );

    float d1a= sdBox( p, vec2(2.0,1.5) );
    float d1b= sdBox( p, vec2(1.9,1.4) );
    d1b= differenceSDF(d1a, d1b);

    float d2= sdRhombus(p, vec2(1.0,0.8));
    float d3= sdBox( p, vec2(2.0,0.05) );
    float d4= sdBox( p, vec2(0.05,1.5) );
    vec3 col;

    col= DrawFigBorde(vec3(1.0), col, d1a);
    col= DrawFigBorde(vec3(0.0), col, d1b);
    col=DrawFigBorde(vec3(0.0), col,d3);
    col=DrawFigBorde(vec3(0.0), col,d4);
    col= DrawFigBorde(vec3(0.5), col, d2 );

    return col;
}

vec3 figura002(vec2 p)
{
    p.x= opRep1D(p.x, 4.0 );
    p.y= opRep1D(p.y, 3.0 );

    float d1a= sdBox( p, vec2(2.0,1.5) );
    float d1b= sdBox( p, vec2(1.9,1.4) );
    d1b= differenceSDF(d1a, d1b);

    float d2= sdRhombus(p, vec2(1.0,0.8));
    float d3= sdBox( p, vec2(2.0,0.05) );
    float d4= sdBox( p, vec2(0.05,1.5) );

    vec3 col;
    col=DrawFigSolo(vec3(0.0), col, d1a);
    col=DrawFigSolo(vec3(1.0), col, d1b);
    col=DrawFigSolo(vec3(1.0), col,d3);
    col=DrawFigSolo(vec3(1.0), col,d4);
    col=DrawFigSolo(vec3(1.0), col, d2 );

    return col;
}


vec3 GetColor(vec3 p, float id_color)
{  vec3 color; 
   if(id_color<50.0)
   { color=getColor(int(id_color)); }  

   if(id_color==57.0)
   { color =vec3(1.0)*( 0.2 + 0.5*mod(floor(p.x*0.25) + floor(p.z*0.25), 2.0));}


   if (id_color==64.0)
   {   
       color = figura001(p.xz*0.25+vec2(1.0,0.0)); }

   if (id_color==65.0)
   { 
   p.xy-=vec2(0.0,5.5);
   color = figura001(p.xy*0.25+vec2(-2.,0.0)); }

   if (id_color==66.0)
   { 
       p.zy-=vec2(0.0,5.5);  
       color = figura001(p.zy*0.25); }

   if (id_color==67.)
   { color = figura002(p.xz*0.25+vec2(0.0,0.5)); }

   return color;
}

vec3 render(vec3 ro, vec3 rd) 
{
    vec3 col = vec3(0.45, 0.8, 1.0);
	vec3 lig1 = normalize(vec3(10.8, 10.0, -15.6));
    vec3 lig2 = normalize(vec3(0.0, 60., 25.6));
    vec2 t;
    vec3 rcol = vec3(0);

    	for(int i = 0; i < 3; i++) {
		 t = RayMarch(ro, rd, MAX_DIST);
		if(t.x < MAX_DIST) 
        {   // geometry, hit position, normal, reflect
			vec3 pos = ro + rd*t.x;
			vec3 nor = GetNormal(pos);
			vec3 ref = reflect(rd, nor);
              vec3 color= GetColor(pos,t.y);
            rcol= lightingv3(nor,pos,lig1,rd, ro, color );
            rcol+= lightingv3(nor,pos,lig2,rd, ro, color );
            rcol/=2.0;

            // set up the ray orgin and direction for reflection.
			ro = pos + nor*0.001;
            if (t.y>70.0)
			rd = ref;
            // sky fog.
			///rcol = mix(rcol, vec3(0.45, 0.8, 1.0), 1.0 - exp(-0.00715*t.x));
            // lighten intensity on each successive reflect.
			if(i == 0)
               col = rcol;
			else
               col = mix(rcol, col, 1.0 - exp(-0.8*float(i)));
		}
        else if(t.x>MAX_DIST)
        col = mix(render_sky_color(rd), col, 1.0 - exp(-0.8*float(i)));

	}
    return col;
}


vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }


vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43; 
    d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

Ray RotarEnCirculo(vec3 ro,vec3 rd)
{
   float r=28.0;
   float veltime=30.0;  
    float tt = radians( -iTime*veltime);
    vec3  pos=vec3(0.0,0.0,10.0);
    vec3 rotation1 = pos+vec3(r*sin(tt), 0.0, r*cos(tt));
    ro +=rotation1;   
    rd=rotate_y( vec3(-rd.x,rd.y,-rd.z), tt);
    return  Ray(ro,rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0.0,8.0,-15.0);
    vec3 rd=normalize(vec3(p,1.0));

   Ray ray= RotarEnCirculo(ro,rd);
    ro=ray.ro;
    rd=ray.rd;
    vec3 col= render(ro, rd);
 	col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;
	fragColor = vec4(col, 1);
}
