const float angle = 1.;
const mat2 m1 = mat2(cos(angle),-sin(angle),sin(angle),cos(angle));

vec3 transform(vec3 p) {
    mat2 m2 = mat2(cos(iTime),-sin(iTime),sin(iTime),cos(iTime));
    p.yz *= m1;
    p.xy *= m2;
    return p;
}

const float slopeUB= 2.;
const float g = sin(atan(1.,slopeUB));

float sde(vec3 p) {
    vec2 uv = p.xy * 0.1 + 0.5;
        float h = 2.5 * texture(iChannel0, uv).r;
    if(uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0 || uv.y > 1.0) {
        h = 0.0;
    }
    return (p.z-h)*g;

}

vec3 rayMarch(vec3 ro, vec3 rd) {
    vec3 p = ro + rd*0.001;
    while(true) {
        float d = 0.25 * sde(p);
        if (d < 0.0001) {break;}
        p += rd*d;
        if (length(p-ro) > 200.) {break;}
    }
    return p;
}

const vec2 helper = vec2(1e-5,0);

float light(vec3 l, vec3 p) {
    float d = sde(p);
    vec3 norm = normalize(vec3(
        10.0 * (texture(iChannel0, p.xy * 0.1 + 0.5 + vec2(1.0 / iResolution.x, 0.0)).r - texture(iChannel0, p.xy * 0.1 + 0.5 - vec2(1.0 / iResolution.x, 0.0)).r),
        10.0 * (texture(iChannel0, p.xy * 0.1 + 0.5 + vec2(0.0, 1.0 / iResolution.y)).r - texture(iChannel0, p.xy * 0.1 + 0.5 - vec2(0.0, 1.0 / iResolution.y)).r) ,
        1.0
        ));
    vec3 ld = normalize(l-p);
    float s = max(0.,dot(ld, norm));
    return s;
}

const vec3 l = vec3(0,-10,5);
const vec3 ro = vec3(0,0,5);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 c = vec3(5.*uv,3);
    vec3 ro_ = ro;
    c = transform(c);
    ro_ = transform(ro_);

    vec3 p = rayMarch(ro_, normalize(c - ro_));
    vec2 uvp = p.xy * 0.1 + 0.5;
    float contour =(uvp.x > 0.0 && uvp.x < 1.0 && uvp.y > 0.0 && uvp.y < 1.0) ? texture(iChannel0, uvp).b: 0.0;
    contour = pow(contour, 5.0);
    contour = smoothstep(0.0, 1.0, contour);
    contour = smoothstep(0.0, 1.0, contour);
    contour = smoothstep(0.0, 1.0, contour);
    vec3 col = vec3(light(l, p));
    col += vec3(0.0, 0.0, contour) * col;

    float d = length(p-ro_);
    col -= vec3(smoothstep(0.,200.,d));
    col = min(vec3(1),col);

    fragColor = vec4(col,1.0);
}

float csdf(vec2 p, vec2 c, float r) {
    return max(length(p - c) - r, 0.0);
}
