    float smin(float a, float b, float k) {
      float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
      return mix(b, a, h) - k * h * (1.0 - h);
    }
float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    //float dist1 = csdf(uv, vec2(0.25, 0.25), 0.1);
    //float dist2 = csdf(uv, vec2(0.75, 0.75), 0.1);
    float num = 1000.0;
    float dom = 1000.0;
    float speed = 0.1;
    for(float i = 0.0; i < 10.0; i++) {
                float dist = csdf(uv, vec2((0.5 + 0.5 * snoise(speed * vec2(10.0 * i, iTime))) * iResolution.x / iResolution.y, (0.5 + 0.5 * snoise(speed * vec2(iTime, i * 10.0)))), 0.0);
        if (mod(i, 2.0) == 1.0) {
            num = num < 1000.0 ? smin(num, dist, 0.1) : min(num, dist);
        } else {
           dom = dom < 1000.0 ? smin(dom, dist, 0.1) : min(dom, dist);
        }
    }
    float final = num / (num + dom);
    fragColor = vec4(vec3(final) + vec3(0.005 * cos(100.0 * final), 0.0, cos(100.0 * final)), 1.0);
}
