vec3 ACESFilm(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 bloom(float scale, float threshold, vec2 fragCoord){
    float logScale = log2(scale)+1.0;

    vec3 bloom = vec3(0);
    for(int y = -1; y <= 1; y++)
        for(int x = -1; x <= 1; x++)
            bloom += textureLod(iChannel0, (fragCoord+vec2(x, y) * scale)/iResolution.xy, logScale).rgb;

    return max(bloom/9.0 - vec3(threshold), vec3(0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    vec3 bloomSum = bloom(.1 * iResolution.y, .9, fragCoord) * .2
        		  + bloom(.2 * iResolution.y, .5, fragCoord) * .2;

    fragColor = vec4(ACESFilm(col + bloomSum), 1.0);
}

#define MaxSteps 64
#define MaxShadowSteps 16
#define AmbientLightSteps 8
#define MaxDist 2.0
#define MinDist .015
#define GIStrength .3
#define AOStrength .52

vec3 CamPos = vec3(0, 0, -17.);
vec3 CamRot = vec3(0);
float CamFocalLength = 01.5;

vec3 LightDir;
vec3 LightColor = vec3(.7, .5, .3) * 10.;
float LightRadius = .05;

float Scale = 2.25;
float Radius = .25;
int Iterations = 9;
mat3 Rotation;

float distanceEstimation(vec3 position){
    position *= Rotation;
	vec4 scalevec = vec4(Scale, Scale, Scale, abs(Scale)) / Radius;
	float C1 = abs(Scale-1.0), C2 = pow(abs(Scale), float(1-Iterations));
	vec4 p = vec4(position.xyz, 1.0), p0 = vec4(position.xyz, 1.0);
	for (int i=0; i< Iterations; i++) {
    	p.xyz = clamp(p.xyz, -1.0, 1.0) * 2.0 - p.xyz;
    	p.xyzw *= clamp(max(Radius/dot(p.xyz, p.xyz), Radius), 0.0, 1.0);
        if(i < 2) p.xyz *= Rotation;
    	p.xyzw = p*scalevec + p0;
	}
	return (length(p.xyz) - C1) / p.w - C2;
}

vec3 normalEstimation(vec3 pos, float dist){
  vec2 k = vec2(dist, 0);
  return normalize(vec3(distanceEstimation(pos + k.xyy),
	  					distanceEstimation(pos + k.yxy),
  						distanceEstimation(pos + k.yyx))
				 - vec3(dist));
}
vec3 normalEstimation(vec3 pos){
  float dist = distanceEstimation(pos);
  return normalEstimation(pos, dist);
}

vec3 backgroundColor(vec3 dir){
    return texture(iChannel0, dir).rgb;
}

//blurred cubemap for ambient lighting
vec3 backgroundColorHemi(vec3 dir){
    return textureLod(iChannel0, dir, 9.5).rgb;
}

vec3 directLight(vec3 pos, vec3 normal){
    float dotLight = -dot(normal, LightDir);
    if(dotLight < 0.0) return vec3(0);
    vec3 pos0 = pos;
    float minAngle = LightRadius;
    for(int i = 0; i < MaxShadowSteps; i++){
        float dist = distanceEstimation(pos);
        if(dist > MaxDist) break;
        if(dist < MinDist) return vec3(0.0);
        pos -= LightDir * dist * 3.0;	//goes 3 times faster since we don't need details
        minAngle = min(asin(dist/length(pos-pos0)), minAngle);
    }
    return LightColor * dotLight * clamp(minAngle/LightRadius, .0, 1.0);
}

vec3 ambientLight(vec3 pos){
    vec3 pos0 = pos;
    float dist0 = distanceEstimation(pos);
    vec3 normal = normalEstimation(pos).xyz, gi, al = vec3(0.0);
    float ao = 1., dist = dist0;
    for(int i = 0; i < AmbientLightSteps; i++){
        float expectedDist = dist * (1. + .8);
        dist = distanceEstimation(pos);
        float weight = AOStrength*(1.-float(i)/float(AmbientLightSteps));	//more weight to first samples
        ao *= pow(clamp(dist/expectedDist, 0., 1.0), weight);
        normal = normalize(normalEstimation(pos, dist)+1.5*normal); //"smoothed" normal to avoid artifacts
        pos += normal * .8*dist; //slightly shorter to avoid artifacts
        al += backgroundColorHemi(normal);
        if(i == 6 || i == 13) gi += ao*directLight(pos, normal); // two GI samples
    }
    gi *= GIStrength/2.0;
    return gi + al/float(AmbientLightSteps) * ao;
}

bool trace(inout vec3 pos, in vec3 dir, out vec3 n){
    for(int i = 0; i < MaxSteps; i++){
        float dist = distanceEstimation(pos);
        if(dist > MaxDist) break;
        if(dist < MinDist){
            n = normalEstimation(pos);
            for(int j = 0; j < 3; j++){
                dist = distanceEstimation(pos) - 2.*MinDist;
        		pos += dist * dir;
            }
            return true;
        }
        pos += dir * dist;
    }
    return false;
}


vec4 rayTrace(vec3 pos, vec3 dir){
    for(int i = 0; i < 5; i++){ //to get to a distance inferior to MaxDist
        float dist = distanceEstimation(pos);
        pos += dist * dir;
        if(dist < MaxDist) break;
    }
    vec3 n;
    if(!trace(pos, dir, n))
        return vec4(backgroundColor(dir), 20.0);
    return vec4(directLight(pos, n)+ambientLight(pos), length(CamPos - pos));
}

mat3 rotationMatrix(vec3 rotEuler){
    float c = cos(rotEuler.x), s = sin(rotEuler.x);
    mat3 rx = mat3(1, 0, 0, 0, c, -s, 0, s, c);
    c = cos(rotEuler.y), s = sin(rotEuler.y);
    mat3 ry = mat3(c, 0, -s, 0, 1, 0, s, 0, c);
    c = cos(rotEuler.z), s = sin(rotEuler.z);
    mat3 rz = mat3(c, -s, 0, s, c, 0, 0, 0, 1);

    return rz * rx * ry;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float time = iTime + 43.;

    //fractal animation
    Rotation = rotationMatrix(vec3(time, time*.7, time*.4)*.2);
    Scale += sin(time*.5)*.25;
    Radius += cos(time) *.25;

    //light animation
    LightDir = normalize(vec3(sin(time*.6), -1, cos(time*.6)));

    //camera animation
    CamRot.z = sin(time*.4)*.2;

    vec2 uv = (fragCoord-iResolution.xy/2.0) / iResolution.y;
    vec3 rayDir = normalize(vec3(uv, CamFocalLength)) * rotationMatrix(CamRot);

    fragColor = rayTrace(CamPos, rayDir);

}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    vec3 skyCol = vec3(80,180,255)/ 255.0;
    vec3 horizonCol = vec3(255,255,255)/ 255.0;
    vec3 groundCol = vec3(0,0,35)/ 255.0;

    vec3 sunCol = vec3(1.0, 1.0, 1.0);
    float sunSize = 0.0005;
    float sunBlur = 0.0025;
    float sunScatter = 0.25;

    vec3 moonCol = vec3(1.0, 1.0, 1.0);
    float moonSize = 0.00025;
    float moonGlow = 0.05;
    float moonShadow = 0.5;
    float starSize = 0.04;

    vec3 sunPos = _sunPos(iTime); 
    vec3 moonPos = _moonPos(iTime);
    vec3 sunDir = normalize(sunPos - rayOri); 
    vec3 moonDir = normalize(moonPos - rayOri); 
    float pitch =  0.5 + rayDir.y * 0.5;

    // sky
    // -----------------------------------------------------------
    vec3 col = skyCol;
    horizonCol = mix (vec3(255,55,0)/ 255.0, vec3(255,255,255)/ 255.0, smoothstep(-0.5, 0.75, sunPos.y));
    horizonCol = mix (vec3(55,155,155)/ 255.0, horizonCol, smoothstep(-0.5, 0.0, sunPos.y));        
    col = mix(horizonCol, col, smoothstep(0.4, 0.8, pitch));    
    col = mix(groundCol, col, smoothstep(0.49, 0.5, pitch));

    // sun
    // ------------------------------------------------------------
    sunCol = mix(vec3(255,85,0)/ 255.0, vec3(255,255,255)/ 255.0, max(0.0, min(1.0, smoothstep(-0.4, 0.8, sunPos.y))));
    float sun = dot(sunDir, rayDir);    // return [-1,1] based on angle 
    sunSize += sunSize * 100.0 * (1.0-smoothstep(-0.8, 0.11, sunPos.y)); // scale sun based on height
    float sunDisk = smoothstep((1.0 - sunSize) - sunSize*2.0, 1.0 - sunSize, sun); // define sun disk
    sunScatter = smoothstep((1.0 - sunSize) - sunScatter, 1.0 - sunSize, sun) *  smoothstep(-0.8, 0.2, sunPos.y); 
    float sunGlow = smoothstep((1.0 - sunSize) - sunBlur, 1.0 - sunSize, sun);

    float haloSize = 0.02;
    float sunHalo = smoothstep(0.9999 - haloSize, 0.9999, sun);
    sunHalo = mix(0.055, 0.05, fract(sunHalo*2.) );

    //float angle = acos( dot(sunDir, rayDir) / (length(sunDir)*length(rayDir)) );
    //float sunFlare = smoothstep((1.0 - sunSize*2.0) - 0.05*smoothstep(0.0, 3.14*.25,angle*10.) , 1.0 - sunSize*2.0, sun);
    float horizonMask = smoothstep( -0.2, 0.05, rayDir.y); // mask sun along horizon
    sunDisk *= smoothstep( -0.05, 0.025, rayDir.y);
    sunGlow *= smoothstep( -0.05, 0.025, rayDir.y);
    sunHalo *= smoothstep( -0.2, 0.05, rayDir.y); // mask sun along horizon

    col = mix(col, sunCol, sunScatter*.25);
    col = mix(col, sunCol, sunGlow*.5);
    col += sunDisk + sunScatter*.05 + sunGlow*.05;
    col += vec3(sunHalo*6.0, 0.0,0.0);

    // night
    // ------------------------------------------------------------
    col *= max(0.35, min(1.0, smoothstep(-1.0, 0.0, sunPos.y)));

    // stars
    // ------------------------------------------------------------
    float starNoise = random3D( rayDir, vec3(0.5,0.1,7.0+ iTime*0.001));
    vec2 starCoords = vec2(fract(rayDir.x * 50.0), fract(rayDir.y * 50.0) );  
    float stars = step(0.6, starCoords.y) * (1.0-step(0.6 + starSize, starCoords.y)) * step(0.3, starCoords.x) * (1.0- step(0.3 + starSize, starCoords.x));
    stars *= smoothstep(-0.01, 0.00, rayDir.y);
    col += stars * pow(starNoise, 3.0) * (1.0-max(0.0, min(1.0, smoothstep(-1.0, 0.0, sunPos.y))));

    horizonMask = smoothstep( -0.01, 0.01, rayDir.y); // mask sun along horizon
    // moon
    // -------------------------------------------------------------
    float moon = dot(moonDir, rayDir);
    moonShadow = mix(-0.05, -0.0, moonShadow);
    moonShadow = dot(vec4(rotY(moonShadow)*vec4(moonDir,1.0)).xyz, rayDir);
    float moonDisk = smoothstep(1.0-moonSize-0.00025, 1.0-moonSize, moon); 
    moonShadow = smoothstep(1.0-moonSize*2.0-0.00025, 1.0-moonSize*2.0, moonShadow); 
    moonDisk *= (1.0 - moonShadow*.995) * horizonMask  * smoothstep(0.0, 0.3, moonPos.y);    
    col = mix(col, moonCol, moonDisk);
    col += moonDisk;

    moonGlow = smoothstep(1.0-moonSize-moonGlow, 1.0-moonSize, moon) * horizonMask  * smoothstep(0.0, 0.3, moonPos.y); 
    col = mix(col, moonCol, moonGlow*.025) ; 


    // Output to cubemap
    fragColor = vec4(col,1.0);
}

//first DoF pass

#define Aperture .05
#define DoFClamping .35
#define DoFSamples 32

#define dir normalize(vec2(1.0, 1.0))


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    //autofocus
    float focalDistance = texture(iChannel0, vec2(.5), 5.0).a;

    vec4 col = vec4(0);
    float samples = 0.0, dr, influence, depth;
    vec2 d;
    vec4 p;
    for(int i = 0; i < DoFSamples; i++){
        d = dir * float(2*i-DoFSamples)/float(DoFSamples) * DoFClamping;
        p = texture(iChannel0, (fragCoord + d*iResolution.y*Aperture)/iResolution.xy);
        dr = min(abs(p.a-focalDistance)/p.a, DoFClamping);
        influence = clamp((dr - length(d))*iResolution.y*Aperture + .5, 0.0, 1.0) / (dr*dr+.001);
        col += influence * p;
        samples += influence;
    }

    col /= samples;

    fragColor = col;
}

//second DoF pass

#define Aperture .05
#define DoFClamping .35
#define DoFSamples 32

#define dir normalize(vec2(1.0, -1.0))


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    //autofocus
    float focalDistance = texture(iChannel0, vec2(.5), 5.0).a;

    vec4 col = vec4(0);
    float samples = 0.0, dr, influence, depth;
    vec2 d;
    vec4 p;
    for(int i = 0; i < DoFSamples; i++){
        d = dir * float(2*i-DoFSamples)/float(DoFSamples) * DoFClamping;
        p = texture(iChannel0, (fragCoord + d*iResolution.y*Aperture)/iResolution.xy);
        dr = min(abs(p.a-focalDistance)/p.a, DoFClamping);
        influence = clamp((dr - length(d))*iResolution.y*Aperture + .5, 0.0, 1.0) / (dr*dr+.001);
        col += influence * p;
        samples += influence;
    }

    col /= samples;

    fragColor = col;
}

float _viewDepth = -1.;

vec3 _sunPos (float time)
{
time = time * 2.5;
    // cos(time*0.25)*0.85, sin(time*0.25)*0.6-0.2, 0.0
    return vec3(-0.6+sin(time*0.2), cos(time*0.2), sin(time*0.2) );       
}

vec3 _moonPos (float time)
{
    time = time * 2.5;
    time -= 5.;
    return vec3(-2.0+sin(time*0.2)*1.5, cos(time*0.2), sin(time*0.2) );          
}


// interpolation
float gaussianInterpol (float t, float a, float b, float c)
{
    // a - amplitude, b - center pos, c - frequency
    return a * exp(-(t-b)*(t-b) / (2.0*c*c));

}

// fractal noise

float random3D(vec3 xyz, vec3 dir)
{
    float val = dot(xyz, dir);
    return fract(30.0 * sin(val));
}


// transformations

mat4 rotX(float angle)
{
    mat4 rotMatrix = mat4
    (
        vec4( 1.0,        0.0,        0.0, 0.0),
        vec4( 0.0, cos(angle), sin(angle), 0.0),
        vec4( 0.0,-sin(angle), cos(angle), 0.0),
        vec4( 0.0,        0.0,        0.0, 1.0)
    );

    return rotMatrix; 
}

mat4 rotY(float angle)
{
    mat4 rotMatrix = mat4
    (
        vec4( cos(angle), 0.0, -sin(angle), 0.0),
        vec4(        0.0, 1.0,         0.0, 0.0),
        vec4( sin(angle), 0.0,  cos(angle), 0.0),
        vec4(        0.0, 0.0,         0.0, 1.0)
    );

    return rotMatrix; 
}

mat4 rotZ(float angle)
{
    mat4 rotMatrix = mat4
    (
        vec4( cos(angle), sin(angle), 0.0, 0.0),
        vec4(-sin(angle), cos(angle), 0.0, 0.0),
        vec4(        0.0,        0.0, 1.0, 0.0),
        vec4(        0.0,        0.0, 0.0, 1.0)
    );

    return rotMatrix; 
}

// math utils
float magnitude(vec3 a)
{
    return sqrt(a.x*a.x + a.y*a.y + a.z*a.z);
}

vec3 normalized(vec3 a)
{
    return a/length(a);
}
