vec3 render_sky_color(vec3 rd)
{
    float t1=mod(iTime,2.0);
    float t2=mod(iTime,3.0);
    if (t1<t2)
        return vec3(0.0);
    else    
    {
    float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
    return  mix(sky, vec3(0.0), rd.y);
    }
}

//https://www.shadertoy.com/view/7sSXDz
vec3 amigaTexture(vec2 pos, float n)
{
    float l = clamp(sin(pos.x*n)*sin(pos.y*n)*10000.0, 0.0, 1.0);
    return vec3(1.0, 0.0, 0.0)*l+(vec3(1.0, 1.0, 1.0)*(1.0-l));
}


