//operacion de Union  por FabriceNeyret2
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )
#define opU(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}

float unionSDF(float distA, float distB)
	{ return min(distA, distB);}

float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}
//-------------------------------------------
//----------oPeraciones de Repeticion
float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}

///------------------------------------
// object transformation
vec3 rotate_x(vec3 p, float phi)
{
    float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------
vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

///https://www.shadertoy.com/view/Ws3SDl
// signed distance to a pyramid of base 1x1 and height h
float sdPyramid( in vec3 p, in float h )
{
    float m2 = h*h + 0.25;

    // symmetry
    p.xz = abs(p.xz); // do p=abs(p) instead for double pyramid
    p.xz = (p.z>p.x) ? p.zx : p.xz;
    p.xz -= 0.5;

    // project into face plane (2D)
    vec3 q = vec3( p.z, h*p.y-0.5*p.x, h*p.x+0.5*p.y);
    float s = max(-q.x,0.0);
    float t = clamp( (q.y-0.5*q.x)/(m2+0.25), 0.0, 1.0 );
    float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
	float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);
    float d2 = max(-q.y,q.x*m2+q.y*0.5) < 0.0 ? 0.0 : min(a,b);

    // recover 3D and scale, and add sign
    return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));;
}

float opScalePiramide( in vec3 p,  float escala  )
{
    float h=1.0 ;
    float s=escala;
    vec3 ps=p/s;
    return sdPyramid(ps, h)*s;
}
///-----------------------------------
float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}


// path functions 
vec2 path(in float z)
{ 
    vec2 p1 =vec2(2.13*sin(z * .15), 1.74*cos(z * .085));
    vec2 p2 =vec2(2.97*sin(z * .079), 1.23*sin(z * .127));
    return p1 - p2;
}

float pathxconz(in float z)
{ 
    float p1 =2.13*sin(z * .15);
    float p2 =2.97*sin(z * .079);
    return p1 - p2;
}

vec3 piramidePuerta(vec3 p)
{
    vec3 res= vec3(9999.0, -1.0,-1.0);
    float sdpy5= opScalePiramide(  p, 35.  );
    float sdpuerta= sdBox(  p-vec3(0.0,14.0,5.0), vec3(2.0,4.0,20.0) );
    p= rotate_y(p-vec3(-10.0,0.0,-20.0), radians(90.0));
    float sdpy6= sdTriPrism(  p-vec3(0.0,0.0,-10.0), vec2(10.0,4.0) );

    vec3 pr=p;
    pr.xy= rotatev2(pr.xy, radians(180.0));
    float sdpy7= sdTriPrism(  pr-vec3(-8.7,-5.0,-10.0), vec2(10.0,4.0) );
    sdpy5= differenceSDF(sdpy5,sdpuerta);
    float sdpy7y6= unionSDF(sdpy7, sdpy6);

    res =opU3(res, vec3(sdpy7y6,-1.0,8.0));
    res =opU3(res, vec3(sdpy5,-1.0,8.0));
    res =opU3(res, vec3(sdpy7,-1.0,8.0));
    return res;
 }   

///------------------------------------
vec3 GetDist(vec3 p  ) 
{

	vec3 res= vec3(9999.0, -1.0,-1.0);
    vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf
    res =opU3(res, vec3(planeDist1,-1.0,6.0)); //inf

   vec3 pb=p;
   vec3 pc=p;
   pb.x+= pathxconz(pb.z);
   pb.x=abs(pb.x)-25.0;
   pb.z= opRep1D( pb.z, 20.0 );

   pc.x+= pathxconz(pc.z);
   pc.x=abs(pc.x)-27.0;
   pc.z= opRep1D( pc.z, 20.0 );

   float banqueta1= sdBox(pb, vec3(2.0,5.0,10.0) );
   float banqueta2= sdBox(pc, vec3(2.0,7.0,10.0) );

   p.z= opRep1D( p.z, 65.0 );
   vec3 pip= piramidePuerta(p);

    res =opU3(res, pip);
    res =opU3(res, vec3(banqueta1,6.0,-1.0));
    res =opU3(res, vec3(banqueta2,7.0,-1.0));
      //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}


float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*dO;
        dS = GetDist(p);
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
        {   mObj.hitbln = true; minDist = abs(dO); break; }

        if(dO>MAX_DIST)
        { mObj.hitbln = false;    minDist = dO;    break; } 
        dO += dS.x;
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return dO;
}
//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
