void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*8.0,500.0);
    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(0.0, 30.0, 20.0 ); light_color1=vec3( 1.0 );
 	light_pos2= vec3( 0.0, 40.0, -20.0 ); light_color2 =vec3( 1.0 ); 
    light_pos3= vec3( 10.0, 15.0, 0.0 ); light_color3 =vec3( 1.0 ); 


   vec3 ro=vec3(0.0,12.0,-40.0+t);
     vec3 rd=normalize( vec3(uv.x,uv.y,1.0));


    light_pos1+=ro;
    light_pos2+=ro;
    light_pos3+=ro;
    vec3 col= Render( ro,  rd);
    //col = exposureToneMapping(2.0, col);
    col = linear2srgb(col);


    fragColor = vec4(col,1.0);
}
