#define MAX_STEPS 100
#define MAX_DIST 100.
#define MIN_DIST 0.001
#define EPSILON 0.001
#define REFLECT 2

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);
vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;

//operacion de Union  por FabriceNeyret2
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )
#define opU(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdRoundBox( vec3 p, vec3 b, float r )
	{ vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r; }
float sdCylinderYZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.yz),p.x)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xz),p.y)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXY( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xy),p.z)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}
//----------oPeraciones de Repeticion
float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}
vec2 opRep2D( in vec2 p, in vec2 c )
	{ vec2 q = mod(p+0.5*c,c)-0.5*c; return  q ;}
vec3 opRep3D( in vec3 p, in vec3 c )
	{ vec3 q = mod(p+0.5*c,c)-0.5*c; return  q ;}
vec3  opRep(vec3 p, vec3 r)
   { return mod(p,r)-0.5*r; }
///------------------------------------
// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


float sdVesica(vec2 p, float r, float d)
{   p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b)): length(p-vec2(-d,0.0))-r;
}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{   p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  
	float planeDist1 = p.y+0.0;  //piso inf
    p.x= opRep1D(p.x, 30.0 ); 
    p.y=p.y-8.0*sin(iTime);
    vec3 pp=p;

    float t1=mod(iTime, 2.0);
    float t2=mod(iTime, 3.0);

    if(t2<t1)
    res =opU3(res, vec3(planeDist1,-1.0,11.0));
    else
    res =opU3(res, vec3(planeDist1,-1.0,12.0));



    p.y=p.y-8.0;
    p= rotate_z(p, radians(90.0));

    float dv1= sdVesica(p.xy, 6.0, 3.0);
    float dt1=sdTriangleIsosceles(p.xy-vec2(0.0,4.0), vec2(3.0,2.0) );
    float db1=sdRoundBox( p, vec3(10.,10.,2.0) , 0.5);
    float dcy1=sdCylinderXY(p-vec3(-1.5,-1.5,0.0),vec2(0.35,3.5) );
    float dcy2=sdCylinderXY(p-vec3(-1.5,-1.5,0.0),vec2(0.35,2.0) );
    float intdcy2= intersectSDF(dcy2,dv1);
    dv1=differenceSDF(dv1, dcy1);


    p=pp;
    //boca
    float db2a=sdBox(p-vec3(-3.8,8.4,0.0), vec3(1.5,0.25,2.5) );
    float db2b=sdBox(p-vec3(-3.8,7.8,0.0), vec3(1.4,0.45,2.5) );
    float db2c=sdBox(p-vec3(-3.8,7.4,0.0), vec3(1.5,0.25,2.5) );
    float disint1= intersectSDF(dv1, db1);

    disint1=differenceSDF(disint1,dcy1);
    float disint2= intersectSDF(db1,dt1);
    res =opU3(res, vec3(disint1,2.0,-1.0));
    res =opU3(res, vec3(disint2,2.0,-1.0));

    res =opU3(res, vec3(db2b,0.0,-1.0)); //2do centro
    res =opU3(res, vec3(db2a,1.0,-1.0)); //1ero
    res =opU3(res, vec3(db2c,1.0,-1.0)); //3ero
    res =opU3(res, vec3(intdcy2,4.0,-1.0));

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}


float GetShadow(vec3 p, vec3 plig)
{   vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

vec3 amb(vec3 c, float k)
{  return c * k; }

float diff(vec3 p,vec3 lp,vec3 n )
{   vec3 l = normalize(lp - p);
    float dif = clamp(dot(l, n), 0.0, 1.0);
    return dif;
}
float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{ vec3 l = normalize(lp - p);
  vec3 r = reflect(-l, n);
  float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 20.0);
  return spe;
}

float occlusion(vec3 pos, vec3 nor)
{
    float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {

        float hr = 0.01 + float(i) * 0.5 / 4.0;        
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 LightShading(vec3 Normal,vec3 toLight,vec3 toEye,vec3 color)
{
    vec3 toReflectedLight=reflect(-toLight, Normal);
    vec3 diffuse = max(0.,dot(Normal,-toLight))*color;
    float specularf=max(dot(toReflectedLight, toEye),0.0);
    specularf=pow(specularf, 100.0);
    vec3 specular =specularf*vec3(1.0);
    return diffuse + specular;
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col, float t) 
{   vec3 lightPos=lp;
    vec3 hit = ro + rd * t;
    vec3 norm = GetNormal(hit);

    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;


    float occ = occlusion(hit, norm);
     float sh;

    if (mObj.blnShadow==true)
        {
         vec3 r = reflect(rd, norm);
        sh=GetShadow(p,lp);
        sh+= occlusion(hit, lp);
        sh+=occlusion(hit,r);
        sh/=2.0;
        }
    else
        {sh=0.5;}    


    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    float spe = pow(max(dot(reflect(-light, norm), -rd), 0.), 8.);
    vec3 color = col * (dif + .35  + vec3(.35, .45, .5) * spe) + vec3(.7, .9, 1) * spe * spe;
    vec3 l = normalize( p-lightPos);
    vec3 v = normalize( p-ro);

    vec3 col2=LightShading(norm,l,v,col);
    color=(color+col2)/2.0;
    return color*sh+ color*atten * occ;
}



vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos, float tdist)
{  float intensity=1.0;
     vec3 result;
    result = lightingv3( nor, p, plight_pos,  rd,ro, colobj, tdist);
    return result;
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
	return sky;
}


float zigzagv2(vec2 p, float d)
{  // triangle wave
    vec2 q=p;
    q.y-= 0.5;
    q.y -= asin(sin(q.x));
    return min(d, abs(q.y)-0.3) ;
}   


vec3 getMaterial( vec3 pp, float id_material)
{ vec3 col=vec3(1.0);
  vec3 p=pp;
  vec3 l1;


    if (id_material==11.0)
    { 
     vec2 q=p.xy;
    q.y-= 0.5;
    q.y -= asin(sin(q.x));
        //return mix(vec3(0.0,0.0,1.0),vec3(0.0,0.0,0.0),abs(q.y)-0.3  );
        return mix(vec3(1.0),vec3(0.0,0.0,0.0),abs(q.y)-0.3  );
    }

    if (id_material==12.0)
    { 
        vec2 uv=p.xz;
        float d=9999.9;
         for( int i=-20; i<40;i++)
          d= zigzagv2(uv-vec2(0.0,float(i*3)) , d);

       return mix(vec3(1.0),vec3(0.0),d  );
    }
}

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{  	vec3 colobj; 

    if( mObj.hitbln==false) return  render_sky_color(rd);

    if (id_color<100)
		{ colobj=getColor(int( id_color)); }


    if (id_material>-1.0 && id_color==-1)
        { 
            colobj=vec3(0.5);
            colobj*=getMaterial(p, id_material); 
            return colobj;
        }
    return colobj;
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;
   mObj.ro=ro;
   vec3 p;

     float d=RayMarch(ro,rd, MAX_STEPS);

    Obj=mObj;
    if(mObj.hitbln) 
    {   p = (ro + rd * d );  
        vec3 nor=mObj.normal;
        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1,d);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2,d);

        col= result;
    }
    else if(d>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}

vec3 linear2srgb(vec3 c) {
    return mix(
        12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055,
        step(vec3(0.0031308), c));
}

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{    return vec3(1.0) - exp(-hdrColor * exposure);  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*6.0,360.0);
    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(20.0, 20.0, 10.0 ); light_color1=vec3( 1.0 );
 	light_pos2= vec3( -20.0, 30.0, -30.0 ); light_color2 =vec3( 1.0 ); 


   vec3 ro=vec3(0.0+t,7.0,-25.0);
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));

    light_pos1+=ro;
    light_pos2-=ro;
    vec3 col= Render( ro,  rd);
    col = linear2srgb(col);

    fragColor = vec4(col,1.0);
}
