// Desmos graph: https://www.desmos.com/calculator/ley84rd52d

// Comment this out and you will understand why this is useful :D
#define REPARAMETRIZE

// Computes the length between a starting (t1) and ending (t2) position on the curve
float bezierArclength(in float t1, in float t2, in vec2 a, in vec2 b, in vec2 c) {
    vec2 abc = a - 2.0 * b + c;
    vec2 ba = b - a;

    float u = dot(abc, abc);
    float v = dot(abc, ba);
    float w = dot(ba, ba);

    float dis = u * w - v * v; // -discriminant
    vec2 t = (u * vec2(t1, t2) + v) / sqrt(dis);
    vec2 y = asinh(t) + t * sqrt(t * t + 1.0);
    return (y.y - y.x) * dis / pow(u, 1.5);
}

// Newton-Raphson solver for inverse arclength
// Allows position things at precise distances along the curve
// I attempted to optimize it but I might have missed something
// I also might try approximating it differently, this is pretty expensive
float bezierInvArclength(in float d, in vec2 a, in vec2 b, in vec2 c) {
    // Precomputation
    vec2 abc = a - 2.0 * b + c;
    vec2 ba = b - a;

    float u = dot(abc, abc);
    float v = dot(abc, ba);
    float w = dot(ba, ba);

    float dis = u * w - v * v;
    float sdis = sqrt(dis);
    float disu = dis / u;
    float k = dis / pow(u, 1.5);

    // Subtract constant of integration
    float y = v / sdis;
    d += (asinh(y) + y * sqrt(y * y + 1.0)) * k;

    // Five iterations of Newton-Rapshon, might not need that many
    float t = ((sqrt(disu + u * abs(d)) - sqrt(disu)) * sign(d) - v) / u; // Initial approximation
    for (int n=0; n < 5; n++) {
        float y = (u * t + v) / sdis;
        float arclen = (asinh(y) + y * sqrt(y * y + 1.0)) * k; // Arclength
        float arcint = 2.0 * sqrt((u * t + 2.0 * v) * t + w); // Arclength integrand
        t -= (arclen - d) / arcint;
    }

    return t;
}

// ---------- Helper functions ----------
vec3 hue2rgb(in float hue) {
    return abs(2.0 * smoothstep(0.0, 1.0, fract(vec3(hue, hue - 1.0 / 3.0, hue + 1.0 / 3.0))) - 1.0);
    //return clamp(abs(6.0 * fract(vec3(hue, hue - 1.0 / 3.0, hue - 2.0 / 3.0)) - 3.0) - 1.0, 0.0, 1.0);
    //return smoothstep(0.0, 1.0, clamp(abs(6.0 * fract(vec3(hue, hue - 1.0 / 3.0, hue - 2.0 / 3.0)) - 3.0) - 1.0, 0.0, 1.0));
}

// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 p, in vec2 pos, in vec2 size, in int char) {
    p = (p - pos) / size + 0.5;
    if (all(lessThan(abs(p - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel1, p / 16.0 + fract(vec2(char, 15 - char / 16) / 16.0), dFdx(p / 16.0), dFdy(p / 16.0)).r;
        color = mix(color, charColor, val);
    }
}

// ---------- SDFs ----------
// https://iquilezles.org/articles/distfunctions2d
float sdStar5(in vec2 p, in float r, in float rf) {
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x, k1.y);
    p.x = abs(p.x);
    p -= 2.0 * max(dot(k1, p), 0.0) * k1;
    p -= 2.0 * max(dot(k2, p), 0.0) * k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf * vec2(-k1.y, k1.x) - vec2(0.0, 1.0);
    float h = clamp(dot(p, ba) / dot(ba, ba), 0.0, r);
    return length(p - ba * h) * sign(p.y * ba.x - p.x * ba.y);
}

// Returns vec2(shortest distance to the curve, parameter of the nearest position on the curve)
float dot2(in vec2 v) { return dot(v, v); }
vec2 sdBezier(in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3) {
    vec2 c1 = p - v1;
    vec2 c2 = 2.0 * v2 - v3 - v1;
    vec2 c3 = v1 - v2;

    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0 / t3;
    float t1 = (dot(c1, c2) + 2.0 * dot(c3, c3)) / t3;
    float t0 = dot(c1, c3) / t3;

    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    if (qq * 0.25 + ppp / 27.0 > 0.0) {
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = clamp(-2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0, 0.0, 1.0);
        return vec2(length(p - mix(mix(v1, v2, root), mix(v2, v3, root), root)), root);
    }

    else {
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0;
        vec2 roots = clamp(2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0, 0.0, 1.0);
        float d1 = dot2(p - mix(mix(v1, v2, roots.x), mix(v2, v3, roots.x), roots.x));
        float d2 = dot2(p - mix(mix(v1, v2, roots.y), mix(v2, v3, roots.y), roots.y));
        return d1 < d2 ? vec2(sqrt(d1), roots.x) : vec2(sqrt(d2), roots.y);
    }
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 2.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 2.0;
    float unit = 4.0 / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 a = texelFetch(iChannel0, ivec2(0, 0), 0).xy;
    vec2 b = texelFetch(iChannel0, ivec2(1, 0), 0).xy;
    vec2 c = texelFetch(iChannel0, ivec2(2, 0), 0).xy;

    // Draw the hull
    drawSDF(sdLine(uv, a, b), vec3(1.0, 0.0, 0.0));
    drawSDF(sdLine(uv, b, c), vec3(1.0, 0.0, 0.0));

    // Draw the bezier curve
    vec2 bezClosest = sdBezier(uv, a, b, c);
    float bezLength = bezierArclength(0.0, 1.0, a, b, c);
    #ifdef REPARAMETRIZE
    bezClosest.y = bezierArclength(0.0, bezClosest.y, a, b, c) / bezLength;
    #endif
    drawSDF(bezClosest.x - 0.05, hue2rgb(bezClosest.y));

    // Draw the control points
    drawSDF(length(uv - a) - 0.02, vec3(1.0, 0.0, 0.0));
    drawSDF(length(uv - b) - 0.02, vec3(1.0, 0.0, 0.0));
    drawSDF(length(uv - c) - 0.02, vec3(1.0, 0.0, 0.0));

    // Plot rotating stars at regular intervals
    float co = cos(iTime), si = sin(iTime);
    mat2 rot = mat2(co, si, -si, co);

    float du = 1.0 / 5.0;
    #ifdef REPARAMETRIZE
    du *= bezLength;
    #endif

    for (float u=0.0; u < 6.0; u++) {
        float t = u * du;
        #ifdef REPARAMETRIZE
        t = bezierInvArclength(t, a, b, c);
        #endif
        vec2 pos = mix(mix(a, b, t), mix(b, c, t), t);

        mat2 starRot = rot;
        if (int(u) % 2 == 0) starRot = transpose(starRot); // Alternate rotation direction

        drawSDF(sdStar5((uv - pos) * starRot, 0.05, 0.5), vec3(0.25, 0.0, 0.5));
    }

    // Display coordinates of A on the left
    drawChar(color, vec3(0.0, 0.0, 1.0), uv, vec2(-1.1, -0.58), vec2(0.15), 65);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(1.2, 0.6), 100.0, a.x);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(1.2, 0.75), 100.0, a.y);

    // Display coordinates of B in the middle
    drawChar(color, vec3(0.0, 0.0, 1.0), uv, vec2(0.0, -0.58), vec2(0.15), 66);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(0.3, 0.6), 100.0, b.x);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(0.3, 0.75), 100.0, b.y);

    // Display coordinates of C on the right
    drawChar(color, vec3(0.0, 0.0, 1.0), uv, vec2(1.1, -0.58), vec2(0.15), 67);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(-0.6, 0.6), 100.0, c.x);
    color = digitIn(color, vec3(1.0), uv * 0.8 + vec2(-0.6, 0.75), 100.0, c.y);

    fragColor = vec4(color, 1.0);
}

// Handy debugging font by @valalalalala (with my nitpicky formatting :D)
