void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 st = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec4 prevPixel = texture(iChannel0, uv);
    vec2 prevForce = prevPixel.xy;
    prevForce *= .99;
    vec2 newForce = prevForce + vec2(
        Perlin3D(vec3(st * 2., iTime * 5.)),
        Perlin3D(vec3((st + 1000.) * 2., iTime * 5.))
    ) * sin(iTime) * 20. + st * .2;
    fragColor = vec4(newForce.x, newForce.y, 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 st = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);    
    vec2 force = texture(iChannel0, uv).xy;
    vec3 prevColor = texture(iChannel1, uv - force * .00001).rgb;
    prevColor *= .999;
    float dist = length(st * 3.1);
    prevColor += vec3(sin(-iTime * 10. + dist * 50.)) 
        //* smoothstep(1., .9, dist)
        * .05;
    fragColor = vec4(prevColor, 0.0);
}

