// rise sound if necessary.
// The interesting part is in the Sound tab




















































// === icon message: "sound in". link: https://www.shadertoy.com/view/MtyXRW

#define C(c) U.x-=.5; T+= U.x<.0||U.x>1.||U.y<0.||U.y>1. ?vec4(0): textureGrad(iChannel3, U/16. + fract( vec2(c, 15-c/16) / 16.), dFdx(U/16.),dFdy(U/16.) )
#define initMsg vec4 T = vec4(0)
#define endMsg  return length(T.yz)==0. ? 0. : T.x

float message(vec2 U) { // to alter in the icon with the alter message
    vec4 T = vec4(0);   // or: initMsg;
    C(83);C(111);C(117);C(110);C(100);C(32);C(105);C(110); // message "Sound in"
    return length(T.yz)==0. ? -1. : T.x; // or: endMsg;
}

void mainImage( out vec4 O, in vec2 u ) {
    if (iResolution.y<2000.) {float c=message((u/iResolution.y-vec2(.1,.2))*8.);if(c>=0.){O=vec4(c);return;}}


    vec2 uv = u / iResolution.xy;
	O = vec4(uv,0.5+0.5*sin(iTime),1.0);

}

// rise sound gain if necessary.
// NB: sound quality degenerate over time because of float precision of time. fix: https://www.shadertoy.com/view/4ltfRN
// some explanations here: https://www.desmos.com/calculator/hdihomdojm

#define hash(p)  fract(sin( (p) * 12.9898 ) * 43758.5453) // random


vec2 mainSound( int it, float t )
{
    float v = 0., w, A=0., tau = 6.2831853, f, phi, 
          n = mod(floor(t),4.), i0, i1;

    if      (n==0.)  i0 = 1., i1 = 2.;                 // note 1 = f
    else if (n==1.)  i0 = 2., i1 = 3.;                 // note 2 = 2f
    else if (n==2.)  i0 = 1., i1 = 10.;                // harmonics from note 1
    else             i0 = 2., i1 = 10.;                // just droping note 1: still same tune perceived.

    for (float i = i0; i < i1; i++ ) {
        f = 200.;                                      // freq fi in Hz
        phi = 0.;                                      // sync phases
     // phi = tau*hash(i+.5);                          // random phases
        w = 1./(i*i);                                  // weight for freq fi
        v +=  cos( tau * f * i * t  + phi  ) *w;       // weighted wave
        A += w;                                        // cumul weight for normalization
    }

    return .5* vec2(  v / A );
}
