vec3 palette(float d){
	return mix(vec3(0.0,3.02,1.24),vec3(1.,0.01,1.2),d);
}

vec2 rotate(vec2 p,float a){
	float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}

float map(vec3 p){
    for( int i = 0; i<66; ++i){
        float t = iTime*0.006;
        p.xz =rotate(p.xz,t);
        p.xy =rotate(p.xy,t*1.0);
        p.xz = abs(p.xz);
        p.xz-=.16;
	}
	return dot(sign(p),p)/6.;
}

vec4 rm (vec3 ro, vec3 rd){
    float t = 1.;
    vec3 col = vec3(0.006);
    float d;
    for(float i =0.; i<36.; i++){
		vec3 p = ro + rd*t;
        d = map(p)*.98;
        if(d<0.07){
            break;
        }
        if(d>22.){
        	break;
        }
        //col+=vec3(3.6,3.8,3.8)/(400.*(d));
        col+=palette(length(p)*.1)/(222.*(d));
        t+=d;
    }
    return vec4(col,1./(d*20.));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-(iResolution.xy/2.))/iResolution.x;
	vec3 ro = vec3(55.,55.,-13.);
    ro.xz = rotate(ro.xz,iTime);
    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf,vec3(7,3.,17.)));
    vec3 cu = normalize(cross(cf,cs));

    vec3 uuv = ro+cf*3. + uv.x*cs + uv.y*cu;

    vec3 rd = normalize(uuv-ro);

    vec4 col = rm(ro,rd);


    fragColor = col;
}

